insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (25, 'Life of Brian', True, False, False, False, 1979, 8.1, 'Brian Cohen is an average young Jewish man, but through a series of ridiculous events, he gains a reputation as the Messiah. When he''s not dodging his followers or being scolded by his shrill mother, the hapless Brian has to contend with the pompous Pontius Pilate and acronym-obsessed members of a separatist movement. Rife with Monty Python''s signature absurdity, the tale finds Brian''s life paralleling Biblical lore, albeit with many more laughs.', 'tt0079470', '/lSSA64WF0M0BXnjwr2quMh6shCl.jpg');
