insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (73, 'The Evil Dead', True, False, False, False, 1981, 7.5, 'When a group of college students finds a mysterious book and recording in the old wilderness cabin they''ve rented for the weekend, they unwittingly unleash a demonic force from the surrounding forest.', 'tt0083907', '/uYxQ6xhP3WjqPZtxyAOnZQWnZqn.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (74, 'About Time', True, False, False, False, 2013, 7.8, 'The night after another unsatisfactory New Year party, Tim''s father tells his son that the men in his family have always had the ability to travel through time. Tim can''t change history, but he can change what happens and has happened in his own life – so he decides to make his world a better place... by getting a girlfriend. Sadly, that turns out not to be as easy as he thinks.', 'tt2194499', '/ls6zswrOZVhCXQBh96DlbnLBajM.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (75, 'Kung Fu Hustle', True, False, False, False, 2004, 7.7, 'Set in Canton, China in the 1940s, the story revolves in a town ruled by the Axe Gang, Sing who desperately wants to become a member. He stumbles into a slum ruled by eccentric landlords who turns out to be the greatest kung-fu masters in disguise. Sing''s actions eventually cause the Axe Gang and the slumlords to engage in an explosive kung-fu battle. Only one side will win and only one hero will emerge as the greatest kung-fu master of all.', 'tt0373074', '/exbyTbrvRUDKN2mcNEuVor4VFQW.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (76, 'Blue Is the Warmest Color', True, False, False, False, 2013, 7.7, 'Adele''s life is changed when she meets Emma, a young woman with blue hair, who will allow her to discover desire, to assert herself as a woman and as an adult. In front of others, Adele grows, seeks herself, loses herself, finds herself.', 'tt2278871', '/kgUk1wti2cvrptIgUz0VTAtSF6w.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (77, 'Y Tu Mamá También', True, False, False, False, 2001, 7.6, 'In Mexico, two teenage boys and an attractive older woman embark on a road trip and learn a thing or two about life, friendship, sex, and each other.', 'tt0245574', '/izw41PkE8y891yraO6fN1tCNmOG.jpg');
