insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (103, 'The Boy in the Striped Pyjamas', True, False, False, False, 2008, 7.8, 'When his family moves from their home in Berlin to a strange new house in Poland, young Bruno befriends Shmuel, a boy who lives on the other side of the fence where everyone seems to be wearing striped pajamas. Unaware of Shmuel''s fate as a Jewish prisoner or the role his own Nazi father plays in his imprisonment, Bruno embarks on a dangerous journey inside the camp''s walls.', 'tt0914798', '/2C8QCXdMlojTxZjfBlINr4FWcb6.jpg');
