insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (110, 'Baahubali 2: The Conclusion', True, False, False, False, 2017, 8.2, 'When Mahendra, the son of Bahubali, learns about his heritage, he begins to look for answers. His story is juxtaposed with past events that unfolded in the Mahishmati Kingdom.', 'tt4849438', '/sXf30F2HFpsFPXlNz7jpOySSV9I.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (112, 'PK', True, False, False, False, 2014, 8.1, 'This experimental satire is a comedy of ideas about an alien in the city, who asks questions that no one has asked before. They are innocent, child-like questions, but they bring about catastrophic answers. People who are set in their ways for generations, are forced to reappraise their world when they see it from PK''s innocent eyes. In the process PK makes loyal friends and powerful foes, mends broken lives and angers the establishment. PK''s childlike curiosity transforms into a spiritual odyssey for him and millions of others. This offbeat, experimental drama is an ambitious and uniquely original exploration of complex philosophies. It is also a simple and humane tale of love, laughter and letting-go. Finally, it is a moving saga about a friendship between strangers from worlds apart.', 'tt2338151', '/hUitxmc1klkZF4zYHAz3oT9e7mT.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (113, 'Extraction', True, False, False, False, 2020, 6.8, 'A former CIA operative is kidnapped by a group of terrorists. When his son learns there is no plan for his father to be saved, he launches his own rescue operation.', 'tt4382872', '/96FQG1MyR9mJFcvPBZAdfsVqaMO.jpg');
