insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (130, 'Blow', True, False, False, False, 2001, 7.6, 'A boy named George Jung grows up in a struggling family in the 1950''s. His mother nags at her husband as he is trying to make a living for the family. It is finally revealed that George''s father cannot make a living and the family goes bankrupt. George does not want the same thing to happen to him, and his friend Tuna, in the 1960''s, suggests that he deal marijuana. He is a big hit in California in the 1960''s, yet he goes to jail, where he finds out about the wonders of cocaine. As a result, when released, he gets rich by bringing cocaine to America. However, he soon pays the price.', 'tt0221027', '/ii4sylRdQnLFPMCLhaER7vb0J6N.jpg');
