insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (136, 'Tootsie', True, False, False, False, 1982, 7.4, 'Michael Dorsey is an unemployed actor with an impossible reputation. In order to find work and fund his friend''s play he dresses as a woman, Dorothy Michaels, and lands the part in a daytime drama. Dorsey loses himself in this woman role and essentially becomes Dorothy Michaels, captivating women all around the city and inspiring them to break free from the control of men and become more like Dorsey''s initial identity. This newfound role, however, lands Dorsey in a hot spot between a female friend/''lover,'' a female co-star he falls in love with, that co-star''s father who falls in love with him, and a male co-star who yearns for his affection.', 'tt0084805', '/ngyCzZwb9y5sMUCig5JQT4Y33Q.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (138, 'Steve Jobs', True, False, False, False, 2015, 7.2, 'Set backstage at three iconic product launches and ending in 1998 with the unveiling of the iMac, Steve Jobs takes us behind the scenes of the digital revolution to paint an intimate portrait of the brilliant man at its epicenter.', 'tt2080374', '/92vhEJ6xWoDJ8UHECAJaRofaSCZ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (139, 'Tremors', True, False, False, False, 1990, 7.1, 'Hick handymen Val McKee and Earl Bassett can barely eke out a living in the Nevada hamlet of Perfection, so they decide to leave town -- despite an admonition from a shapely seismology coed who''s picking up odd readings on her equipment. Before long, Val and Earl discover what''s responsible for those readings: 30-foot-long carnivorous worms with a proclivity for sucking their prey underground.', 'tt0100814', '/cA4ggkZ3r1d5r9hOAUWC8x5ul2i.jpg');
