insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (147, 'The Patriot', True, False, False, True, 2000, 7.2, 'A deadly virus threatens to wipe out an entire Rocky Mountain town, leaving the town doctor to find some way to escape the soldiers who enforce the town''s quarantine and devise an antidote. Matters take a more deadly turn after the physician is captured by a dangerously unstable band of militia extremists.', 'tt0120786', '/v3fn2mDOGUNL5uEn4A26LccxSVY.jpg');
