insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (468, 'What Lies Beneath', True, False, False, False, 2000, 6.6, 'When Claire Spencer starts hearing ghostly voices and seeing spooky images, she wonders if an otherworldly spirit is trying to contact her. All the while, her husband tries to reassure her by telling her it''s all in her head. But as Claire investigates, she discovers that the man she loves might know more than he''s letting on.', 'tt0161081', '/2L3Cn0CizbSy2E9KJt1SiOSyb6p.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (469, 'Fractured', True, False, False, False, 2019, 6.4, 'May Oster, played by Athena Lebessis, is a beautiful, pensive, somber woman in her mid twenties; a beautiful woman that discovers her boyfriend''s bloody scarf in her apartment following a black-out episode. This unearthed white scarf covered in crimson drives May to attempt suicide. May is taken to a psychiatric ward under the care and influence of Dr. Ballard, a fifty-eight year old psychiatrist/acclaimed author, played by Eric Roberts. May is immediately thrown into seclusion. Detective James Harding, played by Jake Busey, appears to question May about a horrific murder. This questioning thrusts May into narrating an in-depth story about an unnamed woman''s involvement with unsavory characters leading to multiple murders. Will May regain her sanity, restore her normal life and unravel the mystery of her true love? Only time will tell.', 'tt2375844', '/r9MR8FycNImtGo9oXo6UU34RcrM.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (471, 'Neruda', True, False, False, False, 2016, 6.9, 'It’s 1948 and the Cold War has arrived in Chile. In the Congress, prominent Communist Senator and popular poet Pablo Neruda accuses the government of betraying the Party and is stripped of his parliamentary immunity by President González Videla. The Chief of Investigative Police instructs inspector Óscar Peluchonneau to arrest the poet. Neruda tries to escape from the country with his wife, the painter Delia del Carril, but they are forced to go underground.', 'tt4698584', '/uM7y94He4VCdKIVEoaaG8APFc1d.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (472, 'One Day', True, True, False, False, 2011, 7, 'A romantic comedy centered on Dexter and Emma, who first meet during their graduation in 1988 and proceed to keep in touch regularly. The film follows what they do on July 15 annually, usually doing something together.', 'tt1563738', '/n9jMwSg4IavdD8wqdYnyW5w3Mvp.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (473, 'Just Friends', True, False, False, False, 2005, 6.2, 'While visiting his hometown during Christmas, a man comes face-to-face with his old high school crush whom he was best friends with – a woman whose rejection of him turned him into a ferocious womanizer.', 'tt0433400', '/jgs2mdFlx8NJzbxXTHeQxvQbN6n.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (475, 'Harold and Lillian: A Hollywood Love Story', True, False, False, False, 2017, 8, 'At once a chronicle of a remarkable sixty-year partnership and a tribute to the unsung talents behind a staggering number of cinema classics, this loving film introduces the world to storyboard artist Harold Michelson and film researcher Lillian Michelson. Though largely uncredited, they left an indelible impression on films like The Birds, The Graduate and Rosemary''s Baby. Through personal letters, rare storyboards and a wealth of interviews, Daniel Raim pulls the curtain back to reveal their love and work and its impact on Hollywood history.', 'tt4683668', '/I2iRQYtBC0rJVirnoPCKLK6Cfl.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (478, 'Beasts of No Nation', True, False, False, False, 2015, 7.7, 'A drama based on the experiences of Agu, a child soldier fighting in the civil war of an unnamed African country. Follows the journey of a young boy, Agu, who is forced to join a group of soldiers in a fictional West African country. While Agu fears his commander and many of the men around him, his fledgling childhood has been brutally shattered by the war raging through his country, and he is at first torn between conflicting revulsion and fascination Depicts the mechanics of war and does not shy away from explicit, visceral detail, and paints a complex, difficult picture of Agu as a child soldier.', 'tt1365050', '/8CgowbCv28a0laVTnPoOrvzVnB7.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (480, 'Final Destination 2', True, False, False, False, 2003, 6.2, 'When Kimberly has a violent premonition of a highway pileup she blocks the freeway, keeping a few others meant to die, safe...Or are they? The survivors mysteriously start dying and it''s up to Kimberly to stop it before she''s next.', 'tt0309593', '/sDsdhVmGzh4L56QvtLLinvXjDs3.jpg');
