insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (485, 'Elena', True, True, False, False, 2013, 7.6, 'Elena, a young Brazilian woman, travels to New York with the same dream as her mother, to become a movie actress. She leaves behind her childhood spent in hiding during the years of the military dictatorship. She also leaves Petra, her seven year old sister. Two decades later, Petra also becomes an actress and goes to New York in search of Elena. She only has a few clues about her: home movies, newspaper clippings, a diary and letters. At any moment Petra hopes to find Elena walking in the streets in a silk blouse. Gradually, the features of the two sisters are confused; we no longer know one from the other. When Petra finally finds Elena in an unexpected place, she has to learn to let her go.', 'tt2475154', '/41lsqOhF67DfTXZ2i77dtWQFI8U.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (486, 'Underworld: Rise of the Lycans', True, False, False, False, 2009, 6.6, 'A prequel to the first two Underworld films, this fantasy explains the origins of the feud between the Vampires and the Lycans. Aided by his secret love, Sonja, courageous Lucian leads the Lycans in battle against brutal Vampire king Viktor. Determined to break the king''s enslavement of his people, Lucian faces off against the Death Dealer army in a bid for Lycan independence.', 'tt0834001', '/6pLPWF7AXhljLJf8WTli9BuVfyv.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (487, 'Talaash', True, False, False, False, 2012, 7.2, 'A cop, investigating the mysterious death of a filmstar, meets a sex-worker, while he faces some personal problems psychologically. The mystery connects these people in a way, that ultimately changes their lives.', 'tt1787988', '/oCxyN7HmJ7zWp8jtJeMRHABnutF.jpg');
