insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (516, 'Deep Impact', True, False, False, False, 1998, 6.2, 'A seven-mile-wide space rock is hurtling toward Earth, threatening to obliterate the planet. Now, it''s up to the president of the United States to save the world. He appoints a tough-as-nails veteran astronaut to lead a joint American-Russian crew into space to destroy the comet before impact. Meanwhile, an enterprising reporter uses her smarts to uncover the scoop of the century.', 'tt0120647', '/s4jLGKtIAtzdOGgwnkJNRFd6iAj.jpg');
