insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (531, 'The Case for Christ', True, False, False, False, 2017, 6.2, 'Based on the true story of an award-winning investigative journalist -- and avowed atheist -- who applies his well-honed journalistic and legal skills to disprove the newfound Christian faith of his wife... with unexpected, life-altering results.', 'tt6113488', '/hTKwkxMv5MCkN8vMXiEmMmAHafh.jpg');
