insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (555, 'Life As We Know It', True, False, False, False, 2010, 6.5, 'After a distastrous first date for caterer Holly and network sports director Messer, all they have is common is a dislike for each other and their love for their goddaughter Sophie. But when they suddenly become all Sophie has in this world, Holly and Messer must set their differences aside. Juggling careers and social calendars, they''ll have to find common ground while living under the same roof.', 'tt1055292', '/zLzS2aOqKsS0QvEaY6SfBw4VBqh.jpg');
