insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (783, 'Palm Trees in the Snow', True, False, False, False, 2015, 7.4, 'Kilian is a young man who has never left the mountains of Huesca which saw him grow up. In 1953 he will travel to the exotic island of Fernando Poo to work in a cacao field alongside his father and his brother. During 20 years in this island he will undertake a journey towards maturity and knowledge, but will also have to deal with pain and loss.', 'tt3202202', '/alqjIcwpXPTamDoQBxKzmpIo94W.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (784, 'Nacho Libre', True, False, False, False, 2006, 5.7, 'Nacho Libre is loosely based on the story of Fray Tormenta ("Friar Storm"), aka Rev. Sergio Gutierrez Benitez, a real-life Mexican Catholic priest who had a 23-year career as a masked luchador. He competed in order to support the orphanage he directed. The producers are Jack Black, David Klawans, Julia Pistor, and Mike White.', 'tt0457510', '/kh7B91bMl2lZ0mH9WhPfaNUIEQH.jpg');
