insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (1039, 'The Student', True, True, False, False, 2016, 6.9, 'Veniamin, a teenager in the midst of a mystical crisis, has his mother, schoolmates and entire high school turned upside down by his questions. - Can girls go to their swimming classes in bikinis? - Does sex education have a place in school? - Should the theory of evolution be taught as part of the Natural Sciences? The adults are soon overwhelmed by the certitudes of the youngster who swears only by Scripture. No one but Elena, his biology teacher, will alone challenge him on his own ground.', 'tt4874206', '/8bRX3YSXNWzCTMRtlFkWcJJeg1r.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (1040, 'My Entire High School Sinking Into the Sea', True, False, False, False, 2016, 6, 'Best friends Dash and Assaf are sophomores at Tides High, where they author the entire school newspaper. When their editor starts offering Assaf solo assignments, Dash''s jealousy prompts him to publish a fictitious story about Assaf having erectile dysfunction. Later, while rummaging through the school archives, Dash discovers records indicating that the building isn''t up to code. He tries to warn his schoolmates, but after his slanderous attacks on Assaf, nobody believes him. Then it happens: an earthquake sends the school toppling into the Pacific...', 'tt5538568', '/j3hcw7faHyIlVBixdgNMkLW6CzT.jpg');
