insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (1056, 'Into the Inferno', True, False, False, False, 2016, 7.2, 'Werner Herzog and volcanologist Clive Oppenheimer embark upon a global journey exploring some of the world''s most mythical volcanoes in Indonesia, Ethiopia, Iceland and North Korea. Speaking with scientists and indigenous peoples alike, they seek to understand the complex and deeply rooted relationship between mankind and one of nature''s greatest wonders.', 'tt4846318', '/cXuduryeLae5UX5oJUREAwDmcjB.jpg');
