insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (1059, 'The Super', True, False, False, False, 2018, 5.9, 'Joe Pesci stars as Louie Kritski, a heartless landlord who has been so negligent in keeping up his ghetto apartment that he is threatened with jail time. The judge gives him another option, which he accepts -- he must live in his rat-infested hell hole until he brings it up to liveable standards. The judge gives him 120 days, during which time Louie meets many of his tenants, including drug dealer Marlon (Ruben Blades). Over time, Louie grows more sympathetic with their problems and sees the results of his own greediness. Unfortunately, Louie''s father, Big Lou Kritski (Vincent Gardenia), is the real owner of the property, and he resists his son''s entreaties to spend money to clean up the place.', 'tt0103007', '/uJGeLycfDUZPQhBy1orsEh64Fxn.jpg');
