insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (1091, 'Bill Hicks: Sane Man', True, True, False, False, 1989, 8.5, 'The amazing comedian Bill Hicks passed away in 1994, but his legend continues to grow. Sane Man was filmed before Bill recorded "Dangerous," his first comedy album, and is a turning point in Hicks'' career. It was the first complete Hicks show ever filmed and Bill pulled out all the stops for the cameras. Completely focused, a newly-sober Hicks paces the stage like a wild animal riffing effortless', 'tt0287337', '/gN9RtL9u9mHjNH4IA2NH6ocdeyq.jpg');
