insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (1123, 'Habibie & Ainun', True, False, False, False, 2012, 7.6, 'A biopic of Indonesia’s third president, Bacharuddin Jusuf Habibie, illuminates his lifelong devotion to his wife.  The movie, Habibie & Ainun, opens with a rickshaw ride on a rainy night in Bandung, West Java, in the 1960s. Habibie (played by Reza Rahadian) asks Ainun (Bunga Citra Lestari) to marry him and to join him on his trip to Germany.', 'tt2589132', '/eOdYhBFF7vE5v83KVVQfDEyLgEu.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (1124, 'Cop Out', True, False, False, False, 2010, 5.6, 'Detectives Jimmy and Paul, despite nine years as partners, can still sometimes seem like polar opposites – especially when Paul''s unpredictable antics get them suspended without pay. Already strapped for cash and trying to pay for his daughter''s wedding, Jimmy decides to sell a rare baseball card that''s worth tens of thousands. Unfortunately, when the collector''s shop is robbed and the card vanishes with the crook, Paul and Jimmy end up going rogue, tracking down the card and the drug ring behind its theft, all on their own time, and without any backup – except for each other.', 'tt1385867', '/qA31i7FPVDHezuoevx3497CvZd4.jpg');
