insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (1135, 'Billu', True, False, False, False, 2009, 6.2, 'A remake of the hugely successful Malayalam film Kadha Parayumbol, Billu (formerly "Billu Barber") is the story of a small town man, Billu (Irrfan Khan) who mentions to his family that he was once a friend of superstar; Sahir Khan (Shahrukh Khan). Then everything changes when Sahir Khan makes an appearance to film a movie in the town. Rumors spread that Billu is Sahir''s close friends, and he becomes a celebrity virtually overnight. The trouble begins when townsfolk want to meet with Sahir but Billu is unable to contact him.', 'tt1230448', '/nmWKskigmvBow3uOP4RPQOIaUux.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (1136, 'Standoff', True, False, False, False, 2016, 6.1, 'A troubled veteran gets a chance at redemption by protecting a girl from an assassin after she witnesses a murder. Holding a shotgun with a single shell, he engages in physical and psychological warfare in a desperate fight for the girl''s life.', 'tt3703908', '/adlwQTs3zEo8EL2ta1IZebhoVPE.jpg');
