insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (1162, 'Take Me', True, False, False, False, 2017, 6.1, 'Ray is a fledgling entrepreneur who specializes in high-end simulated abductions. He jumps at the chance when a mysterious client contracts him for a weekend kidnapping with a handsome payday at the end. But the job isn''t all that it seems.', 'tt6598626', '/70kL9vXjbCAYd3wNXYScCBGlkJC.jpg');
