insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (1169, 'House at the End of the Street', True, False, False, False, 2012, 5.6, 'A mother and daughter move to a new town and find themselves living next door to a house where a young girl murdered her parents. When the daughter befriends the surviving son, she learns the story is far from over.', 'tt1582507', '/t9E3Inaar1CAn5Cwj0M8dTwtD8H.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (1173, 'All the Boys Love Mandy Lane', True, False, False, False, 2008, 5.5, 'Beautiful Mandy Lane isn''t a party girl but, when classmate Chloe invites the Texas high school student to a bash in the countryside, she reluctantly accepts. After hitching a ride with a vaguely scary older man, the teens arrive at their destination. Partying ensues, and Mandy''s close pal, Emmet, keeps a watchful eye on the young males making a play for Mandy. Then two of the students are murdered.', 'tt0490076', '/8XFpaS8AULOYepxcH1VKwbyIPGL.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (1174, 'Ghost in the Shell Arise - Border 1: Ghost Pain', True, False, False, False, 2013, 7.2, 'The story is set in 2027, one year after the end of the fourth non-nuclear war. New Port City is still reeling from the war''s aftermath when it suffers a bombing caused by a self-propelled mine. Then, a military member implicated in arms-dealing bribes is gunned down.  During the investigation, Public Security Section''s Daisuke Aramaki encounters Motoko Kusanagi, the cyborg, wizard-level hacker assigned to the military''s 501st Secret Unit. Batou, a man with the "eye that does not sleep," suspects that Kusanagi is the one behind the bombing. The Niihama Prefectural Police detective Togusa is pursuing his own dual cases of the shooting death and a prostitute''s murder. Motoko herself is being watched by the 501st Secret Unit''s head Kurutsu and cyborg agents.', 'tt2636124', '/p0kHK9nJoLzpqa6tqOzPgPkdxYN.jpg');
