insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (1179, 'Seal Team Six: The Raid on Osama Bin Laden', True, False, False, False, 2012, 5.7, 'When the rumored whereabouts of Osama bin Laden are revealed, the CIA readies a team of seasoned U.S. Navy SEALs for the mission of a lifetime. Despite inconclusive evidence that bin Laden is inside the compound, and ignoring the possible ramifications of an unannounced attack on Pakistani soil, the Pentagon orders the attack. The SEAL Team bands together to complete their mission of justice in a riveting final showdown.', 'tt2095605', '/hau6iHAIWJmlnaaZ0QRYZu9T6B1.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (1180, 'Radio Rebel', True, False, False, False, 2012, 6, 'High school senior Tara is so painfully shy that she dreads speaking to anyone in the hallways or getting called on in class. But in the privacy of her bedroom with her iPod in hand, she rocks out -- doing mock broadcasts for Miami''s hottest FM radio station, which happens to be owned by her stepfather. When a slot opens up at The SLAM, Tara surprises herself by blossoming behind the mike into confident, "Radio Rebel" -- and to everyone''s shock, she''s a hit!', 'tt2008633', '/3EALFmw1DHfiYFtTzNTKY6yJfzv.jpg');
