insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (1215, 'Bill Hicks: Relentless', True, False, False, False, 1992, 8.7, 'Outspoken and raw, not mincing matters, Bill Hicks tells us that, just like smokers, non-smokers also die every day - with jogging instructor Jim Fixx as an example -, asks women what''s so terrible about giving head, talks us through what really happened during the 1990-1991 Gulf War and how the used technology could have also been used to feed the poor of the world.', 'tt0148668', '/32lUxq4tk5M8FJPRgxPHTwFmbgn.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (1217, 'Bhoot', True, True, False, False, 2003, 6.5, 'Manjeet Khosla lives with her widowed mom in Bombay, India. She gets married, gives birth to a son, and separates from her spouse. She goes to live on the 12th floor of an apartment building. Thereafter, she has an affair with Sanjay Thakkar, who is the son of the building Secretary. Shortly thereafter, she and her son fall down from their balcony and die. The building watchman testifies that Manjeet was depressed and suicidal.', 'tt0341266', '/lHb8qSbtPAzm0yE3JhkN764veH0.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (1219, 'Raajneeti', True, False, False, False, 2010, 7.1, 'It is the story of a fiercely fought election campaign, where money power and corruption are the accepted norms, and where treachery and manipulation are routinely used weapons. As the personal drama of these conflict-ridden characters unfolds against this gritty backdrop, love and friendship become mere baits, and relationships get sacrificed at the altar of political alignments. The darkness that rises from their souls threatens to envelope all that they hold precious. Until eventually, in the crescendo of increasing violence, the line between good and evil blurs, making it impossible to distinguish heroes from villains. Raajneeti is the story of Indian democracy. And its ugly underside. It is about politics. And beyond.', 'tt1291465', '/5Y2a3isdO22b0ELsnwz0KoVGJ3h.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (1220, 'Shepherds and Butchers', True, False, False, False, 2017, 6.8, 'A lawyer takes on a case of a prison guard in South Africa who is traumatized by the executions he''s witnessed.', 'tt4627352', '/9iykrTxX2rvm4LC0EYBvKiKxTpY.jpg');
