insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (1243, 'Jerry Seinfeld: I''m Telling You for the Last Time', True, False, False, False, 1998, 7.9, 'Taped for HBO in August 1998, on the final date of Jerry Seinfeld''s tour appearances at New York City''s Broadhurst Theater, I''m Telling You for the Last Time presents the standup comedian''s so-called "final" standup, or at least his final tour with the standup material that made him famous.', 'tt0500140', '/jffjpqXw6W9x6etUSYBkMPEQWRY.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (1245, 'Barbie: Princess Charm School', True, False, False, False, 2011, 6.4, 'Barbie stars as Blair Willows, a kind-hearted girl who is chosen to attend Princess Charm School: a magical, modern place that teaches dancing, how to have tea parties, and proper princess manners. Blair loves her classes -- as well as the helpful magical sprites and her new friends, Princesses Hadley and Isla. But when royal teacher Dame Devin discovers that Blair looks a lot like the kingdom’s missing princess, she turns Blair’s world upside down to stop her from claiming the throne. Now Blair, Hadley and Delancy must find an enchanted crown to prove Blair’s true identity in this charming and magical princess story!', 'tt2066832', '/lI2jPbssax6XX5vDqB9mTJHGzfH.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (1246, 'Jim Gaffigan: King Baby', True, False, False, False, 2009, 7.8, 'The greatly anticipated follow up to the platinum selling Beyond the Pale. In this Austin performance that capped off the 2008 sold-out stand up tour, Gaffigan does not let the audience catch their breath. This all-new show proves no other comedian working today can romanticize laziness and over-indulgence like Jim Gaffigan.', 'tt1390471', '/wcD3IL6NmsioH5R6qCY4yFijWlx.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (1247, 'The International', True, False, False, False, 2006, 7.2, 'An interpol agent and an attorney are determined to bring one of the world''s most powerful banks to justice. Uncovering money laundering, arms trading, and conspiracy to destabilize world governments, their investigation takes them from Berlin, Milan, New York and Istanbul. Finding themselves in a chase across the globe, their relentless tenacity puts their own lives at risk.', 'tt0963178', '/kBrzK1vyu5n2ZpGfZQkeJU1CT6l.jpg');
