insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (1255, 'Miss Representation', True, False, False, False, 2011, 7.6, 'The film MISS REPRESENTATION exposes how American youth are being sold the concept that women and girls’ value lies in their youth, beauty and sexuality. Explores the under-representation of women in positions of power and influence in America, and challenges the media''s limited portrayal of what it means to be a powerful woman. It’s time to break that cycle of mistruths.', 'tt1784538', '/98owJxMsoARozljBXpQXH93v2fF.jpg');
