insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (1279, 'Sleepover', True, True, False, False, 2004, 5.3, 'As their first year of high school looms ahead, best friends Julie, Hannah, Yancy and Farrah have one last summer sleepover. Little do they know they''re about to embark on the adventure of a lifetime. Desperate to shed their nerdy status, they take part in a night-long scavenger hunt that pits them against their popular archrivals. Everything under the sun goes on -- from taking Yancy''s father''s car to sneaking into nightclubs!', 'tt0368975', '/tU5O6KWJTG8PvLPWzOtabTijAjb.jpg');
