insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (1409, 'Friend Request', True, True, False, False, 2016, 5.3, 'Enjoying college life as a popular student, Laura shares everything with her more  than 800 friends on Facebook. But one day, after accepting a friend request from a  social outcast named Marina, Laura’s life is cursed...', 'tt3352390', '/8sc2FXCczMd4lb8j5YiT0S6aQiO.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (1412, 'When Two Worlds Collide', True, False, False, False, 2016, 7.7, 'In this tense and immersive tour de force, audiences are taken directly into the line of fire between powerful, opposing Peruvian leaders who will stop at nothing to keep their respective goals intact. On the one side is President Alan Garcia, who, eager to enter the world stage, begins aggressively extracting oil, minerals, and gas from untouched indigenous Amazonian land. He is quickly met with fierce opposition from indigenous leader Alberto Pizango, whose impassioned speeches against Garcia’s destructive actions prove a powerful rallying cry to throngs of his supporters. When Garcia continues to ignore their pleas, a tense war of words erupts into deadly violence.', 'tt5278932', '/tuokWtYrJk8JuOaqmmiDl7UdhMC.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (1413, 'Blind Date', True, True, False, False, 2015, 6.6, 'He invents puzzles. He’s committed body and soul to his work and needs silence to be able to concentrate. She is an accomplished pianist and can’t live without music. She must prepare for a competition that could change her life. They are going to be forced to coexist without seeing each other.', 'tt4466936', '/7SeDsqYCmCFO7fNTzQ3GFp97sg8.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (1414, 'Chup Chup Ke', True, False, False, False, 2006, 6.8, 'A debt-ridden young man Jeetu (Shahid Kapoor) decides to commit suicide to allow his father to collect insurance money. Unfortunately, he is rescued by two fishermen, Gundya (Paresh Rawal) and Bandya (Rajpal Yadav). Gundya owes money to a Gujarati businessman, Prabhat Singh Chauhan (Om Puri) and he hatches a scheme to exchange Jeetu for the money he owes. Jeetu does not want to reveal his impoverished state, and therefore pretends to be mute and deaf. A hilarious sequence of events follows...', 'tt0464160', '/daXoADAtEPis5bBvSrXVWfRa77X.jpg');
