insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (1562, 'Lion''s Heart', True, False, False, False, 2013, 6.7, 'Ivana Cornejo is just getting used to being single again after her divorce three years ago. After an exasperating call from her ex-husband, she throws her cell phone away. Fortunately, the man who finds it calls her to return it, and to her amusement they have an instant rapport. The man on the other end of the phone is León Godoy, a renowned architect with a charming voice and a charismatic personality. They schedule a date so that he can return the cell phone. When León arrives, Ivana is surprised: he is everything she had imagined, except for one unexpected and startling detail... he’s only 4''5" tall.', 'tt2948472', '/8RA3ufv26ZvZnzWWkNCCgUjveFI.jpg');
