insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (1642, 'Gie', True, False, False, False, 2005, 7.6, 'Gie is a 2005 Indonesian film directed by Riri Riza. The film tells the story of Soe Hok Gie, a graduate from University of Indonesia who is known as an activist and nature lover. The film is based on a diary Catatan Seorang Demonstran written by Soe himself. The plot of this film is an interpretation of the filmmakers, and scenes portraying Soe''s private life may be partly fictionalised for dramatisation.', 'tt0459327', '/vYJTJzwS81gXTDV28U2zxrFtEi5.jpg');
