insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (1652, 'Screwed', True, True, False, False, 2000, 5.8, 'The film is a semi-biographical story based on the experiences of former prison guard Ronnie Thompson who spent seven years working in some of the UK''s most dangerous prisons. Based on Thompson''s book of the same name, the project stars James D''Arcy (Master &amp; Commander), Noel Clarke (Kidulthood), Frank Harper (The Football Factory), Jamie Foreman (Layer Cake), Andrew Shim (This Is England) and Kate Magowan (Stardust). The story revolves around former soldier Sam Norwood who takes a job as a prison officer when he returns from Iraq and becomes exposed to the underworld of prison culture - including corrupt guards and drug trafficking.', 'tt1727816', '/iaBFrbZctQcKJ0s7APpXmAlmPcB.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (1653, 'Martial Arts of Shaolin', True, True, False, False, 1986, 6.6, 'Jet Li weasels out of the north Shaolin temple to assassinate a despotic ruler at the ruler''s extravagant public birthday celebration. Two other men from the south Shaolin temple also set out to assassinate the ruler, but all three fail and are chased all over by soldiers. Meanwhile, one of the southerners turns out to be a cross-dressed woman, who is also discovered to wear a footbell to match Jet Li''s, meaning they are somehow slated for an arranged marriage.', 'tt0091607', '/5Gk38G7JcGJY6aR4cdrZ8sftSsv.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (1655, 'Mike Birbiglia: What I Should Have Said Was Nothing', True, True, False, False, 2008, 7.6, 'Mike says, "A few years ago my therapist suggested I keep a journal of all the crazy things that were going on in my life, so that I could keep things in perspective. Around the same time audiences were demanding more material, and I realized that other people might enjoy these stories-so I started sending them out to my mailing list. Now, my Secret Public Journal has become a Comedy Central special and DVD for all the world to see. Not sure this is what my therapist had in mind."', 'tt1188112', '/mRHqImaRNU096gEyiE0lMZqW5I4.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (1659, 'Patton Oswalt: Talking for Clapping', True, False, False, False, 2016, 6.8, 'Patton Oswalt delivers a fresh hour plus of stand-up, covering everything from misery to defeat to hopelessness. It''s his most upbeat special to date.', 'tt5457520', '/8HkkLy2T0YHxAJGBbsfoXVID7yf.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (1661, 'Return to the 36th Chamber', True, True, False, False, 1980, 7, 'The workers of a dye factory have their pay cut by 20% when the factory owner brings in some Manchu thugs to try and increase production. Desperate to reclaim their full wages, the workers hire an actor to impersonate a priest and kung-fu expert from the temple of Shaolin. The factory owner proves the actor a fraud, and punishes all those involved. The young actor feels he has let the workers down, and promises to atone. He sets out for Shaolin, determined to be accepted as a kung-fu pupil at the elite temple.', 'tt0081494', '/dg0bCE0a4p9IUGQLB1AMnd7WACq.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (1662, 'The Ridiculous 6', True, False, False, False, 2015, 4.8, 'When his long-lost outlaw father returns, Tommy "White Knife" Stockburn goes on an adventure-filled journey across the Old West with his five brothers.', 'tt2479478', '/yN8jrNA9TeyeBr80SgFvBpEIFor.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (1663, 'Shootout at Lokhandwala', True, False, False, False, 2007, 7.1, 'On a calm summer day in 1991, in the bustling Lokhandwala Complex, five criminals including Maya and Dilip were counting 70 lakhs in flat no. 32 B, when 286 policemen, headed by ACP Khan, took strategic positions around their building. A gunfire ensued and the entire nation witnessed the most talked about daylight encounter lasting 6 hours that transformed suburban Mumbai into a war zone.', 'tt0811066', '/6p8ddG6t8FzJTFNYODG27Er0Pdn.jpg');
