insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (1669, 'Sebastian Maniscalco: What''s Wrong with People?', True, False, False, False, 2012, 8.1, '"What''s Wrong with People?" asks Sebastian Maniscalco, as he hilariously tries to bridge the Italian-American Old World he grew up in with the contemporary frenetic world we all live in today.', 'tt2215489', '/uCWYMzbWEq1K8H9M6L0XKJqwy75.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (1670, 'My Little Pony: Equestria Girls - Friendship Games', True, False, False, False, 2016, 6.5, 'Canterlot High meets its rival school, Crystal Prep Academy, in a competition that’s a long-standing tradition – The Friendship Games. Sunset Shimmer is tasked with keeping magic out of the games to avoid the appearance of impropriety while she and her friends compete against Crystal Prep’s top students led by someone with an equal amount of interest in Equestrian magic – this world’s TWILIGHT SPARKLE.', 'tt4450396', '/ih6vNDVWAeSYJYoQAk03Gykl1DJ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (1673, 'The Gathering', True, False, False, False, 2003, 7.2, 'Cassie Grant (Christina Ricci) is a young girl from the United States who is wandering through England on foot. On her way to Ashby Wake Cassie is hit by a car. The driver of the car, Mrs Marion Kirkman (Kerry Fox), immediately calls an ambulance. During an examination at the local hospital the doctor comes to the conclusion that Cassie only has some scratches and not even a concussion, but Cassie has lost her memory due to the accident. She only knows her name and mother country, but she does not know which town she comes from, who her family is and why she is in England.', 'tt0294594', '/vkzhBNcjpE5BhxaxzrY4U5nLMQ3.jpg');
