insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (1679, 'The Eichmann Show', True, False, False, False, 2015, 6.5, 'The behind-the-scenes true life story of a groundbreaking producer, Milton Fruchtman, and blacklisted TV director Leo Hurwitz who, overcoming enormous obstacles, set out to capture the testimony of one of the war''s most notorious Nazis, Adolf Eichmann, who is accused of executing the ''final solution'' and organising the murder of 6 million Jews. This is the extraordinary story of how the trial came to be televised and the team that made it happen.', 'tt4163668', '/bjhJTAuvDASuJfANVvule3uh4MZ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (1680, 'Boy Missing', True, False, False, False, 2016, 6.4, 'Víctor is a child who is found casually roaming by a country road. His mother, Patricia, a well-known lawyer specialist in media trails, realizes about it and she goes to the hospital, where explains to the police that Víctor is deaf, and he''s unable to talk, speaking using sign language. In the police headquarter Víctor explains that he was kidnapped by a strange man when we was entering to the school. The police identifies a possible suspect named Charlie, a man with money troubles and an 8-months pregnant wife who can''t explain where he was when Víctor was kidnapped from the school. Seeing the inability of the police to arrest Charlie after some failed attempts to meet Víctor, Patricia contacts Raúl, Patricia''s former love interest and Víctor''s father, to ask him that he or a friends of him threat seriously Charlie. But when is discovered that Víctor made a false identification, Patricia tries to stop Raúl, starting a chain of events which consequences will be out of control...', 'tt4621630', '/4BLDTCJSbBSRNJFd9Q5Za10grCA.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (1686, 'Sarah Silverman: A Speck of Dust', True, False, False, False, 2017, 6.8, 'In her first comedy special post-health scare, Sarah Silverman shares a mix of fun facts, sad truths and yeah-she-just-went-there moments.', 'tt6948354', '/bvTyPBdS4tiJ4Nv2zPnT9kAjSoD.jpg');
