insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (1707, 'Ghayal', True, True, False, False, 1990, 7.6, 'Ajay Mehra leaves for Bombay in order to train to become an international boxer. On his return to New Delhi he however found that his brother Ashok Mehra is missing, he decides to make complaints at the police station but this only leads to frustration. He decides to go and search for his brother himself but still does not find him. Later on Ashok''s body is found and Ajay is accused of killing him as well as having an illicit relationship with his sister - in - law Indu Mehra. While in jail Ajay makes friends with some other hardcore convicts, who are good at heart. Then one day they escape the jail by overpowering the guards. But Ajay falls in love with Varsha a journalist who helped in showing Ajay the path to justice in killing the main villain Balwant Rai.', 'tt0099652', '/r8n2JqfyxoL1qBEp8JnnyiFg2fU.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (1709, 'The Adderall Diaries', True, False, False, False, 2016, 5.2, 'Writer and Adderall enthusiast Stephen Elliott reaches a low point when his estranged father resurfaces, claiming that Stephen has fabricated much of the dark childhood that that fuels his writing. Adrift in the precarious gray area of memory, Stephen is led by three sources of inspiration: a new romance, the best friend who shares his history, and a murder trial that reminds him more than a little of his own story. Based on the memoir of the same name.', 'tt1735907', '/4fA9YmHYamUlGd4rU0EKtRr5Z1T.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (1710, 'Psycho', True, False, False, False, 2020, 6.3, 'Criminal on the run, Marion Crane takes refuge at the motel operated by Norman Bates - a troubled man whose victims encounter a grisly fate at the hands of his "mother." Marion soon becomes the next victim and her disappearance prompts inquiries from her sister and a private investigator. They both soon discover the morbid bond linking Norman to his mysterious "mother" at the Bates Motel.', 'tt0155975', '/ymealVPnlVEmm7eAGqcy7vCKoxB.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (1719, '300 Miles to Heaven', True, True, False, False, 1989, 7.2, 'Film based on a true story dating back to 1985 when two Polish boys, a teenager and his little brother, escaped from communist Poland. Hiding beneath a lorry they reached Scandinavia - against all odds.', 'tt0096740', '/3i2tszRVYsaa5CYGhOsKAWvDCbi.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (1721, 'Shorts', True, False, False, False, 2009, 4.7, 'A young boy''s discovery of a colorful, wish-granting rock causes chaos in the suburban town of Black Falls when jealous kids and scheming adults alike set out to get their hands on it.', 'tt1100119', '/bKnQostymQJY6l3nrdQZ4iIhLSk.jpg');
