insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (1734, 'Miss Julie', True, False, False, False, 2014, 5.4, 'Julie is a young tennis star who falls in love with his trainer - because she yearns for love and because she wants to liberate themselves from the ambitions which the father has on her behalf.  Birgitte Hjort Sørensen has a conflict and fragile Juliet, and Rolf Hansen is the coach who tries to keep Julie from life. Other actors, Jesper Christensen as ambitious and indebted father, Trine Appel as the coach''s disdain fiance and Niels Skousen as a nightclub owner. "Julie" is a modern update of August Strindberg''s "Miss Julie".', 'tt1784486', '/trLjmdRw52Kpilwruu7kFjrne0D.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (1735, 'Why We Fight: Prelude to War', True, True, False, False, 1942, 7, 'Prelude to War was the first film of Frank Capra''s Why We Fight propaganda film series, commissioned by the Pentagon and George C. Marshall. It was made to convince American troops of the necessity of combating the Axis Powers during World War II. This film examines the differences between democratic and fascist states.', 'tt0035209', '/vpkCDbVQDXth2lK3Zw5FYcxAvX3.jpg');
