insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (1744, 'The Bye Bye Man', True, False, False, False, 2017, 4.2, 'When three college students move into an old house off campus, they unwittingly unleash a supernatural entity known as The Bye Bye Man, who comes to prey upon them once they discover his name. The friends must try to save each other, all the while keeping The Bye Bye Man''s existence a secret to save others from the same deadly fate.', 'tt4030600', '/bape74Eeli5NJ6x4UKuUr604xXh.jpg');
