insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (2349, 'Romantik Komedi', True, False, False, False, 2010, 5.3, 'Esra, Didem ve Zeynep çok yakın arkadaş olmalarına rağmen birbirinden farklı hayatlar sürdüren farklı karakterlere sahip üç kişidir. Sessiz sakin olan Esra, deli dolu olan� Didem ve dominant karaktere sahip olan Zeynep.. Bu üç arkadaştan istikrarlı olarak ilişkisini sürdürebilmiş olan Zeynep''in düğünü hem Esra''nın hem de Didem''in hayatında değişik süprizlere neden olacaktır. İstemediği bir işte yıllardır çalışan Esra bir cesaret örneği göstererek, hayatını değiştirmeye karar verir. Bunun için ilk adımı atar ve işinden istifa eder, kötü giden ilişkisini sona erdirir. Uzun iş aramalarından sonra zar zor iş bulduğu reklam ajansına çalışmaya başlayana Esra''yı bir sürpriz bekliyordur.', 'tt1587263', '/8JTvDkPBrS3RUudeJFZ55EPTODo.jpg');
