insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (2376, 'The Trap', True, True, False, False, 2019, 4.3, 'Lawyer Ralph Anderson arrives in Tula, an amazingly remote town in the desert, as reluctant emissary of mob chief Victor Massonetti, who wants the airstrip clear for his unofficial exit from the country. Ralph''s arrival has a profound effect on his estranged father, the sheriff; his brother Tip, an alcoholic deputy; and his ex-sweetheart Linda, now married to Tip. Tension builds as a small army of gangsters takes over the town. Then the situation abruptly changes...', 'tt0053374', '/erSaBeJUhBpnt0JxJ2XwVtxxjVA.jpg');
