insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (3009, 'Sam Kinison: Family Entertainment Hour', True, True, False, False, 1991, 6.7, 'Get ready to scream with laughter! Sam Kinison''s primal scream will echo down through the comedy hall of fame summing up so much of modernity''s horror and stupidity. This concert was taped at the Wiltern Theatre where Sam was joined with the Randy Hansen Band and a salacious slew of sinuous sirens, including his wife Malika. Join Sam Kinison and guests for a concert guaranteed to make you laugh ''til you cry for more - there''s something for every member of the family - Sam Kinison style!', 'tt0249934', '/hKAhogDrSQDz9ZM3alNYrNH649r.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (3011, 'The Pact', True, True, False, False, 2013, 6.1, 'After their mother passes away, sisters Nicole and Annie reluctantly return to their childhood home to pay their last respects. While staying overnight in the house, the sisters sense a mysterious presence in their midst: noises startling them in the night, objects moving about, a fallen picture of an unknown woman posed next to their mother. Annie begins experiencing a series of intense and disturbing dreams - visions that lead her to uncover something terrible about her mother''s past that is finally revealing itself.', 'tt2040560', '/o0a4iSc5yKEiV15NCTI5GwrZudc.jpg');
