insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (3039, 'Flying Guillotine II', True, True, False, False, 1978, 6.3, 'Take two directors (Cheng Kang, who made the popular true crime thriller "The Criminals", and Hua Shan, who helmed the superheroes "Super Inframan"), a cast of the best and best-loved martial arts actors, then respected kung fu choreographer Tang Chia, and what do you have? The beloved cult sequel to the international "esoteric weapon/crazy cutlery" hit, "The Flying Guillotine", that''s what !', 'tt0075802', '/aTMPSuO3FkSOFlRsaJ2RFyETFau.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (3048, 'X: Past Is Present', True, True, False, False, 2015, 5.4, 'X: Past is Present is a 2015 Indian collaborative feature film directed by a team of eleven filmmakers including Abhinav Shiv Tiwari, Anu Menon, Nalan Kumarasamy, Hemant Gaba, Pratim D. Gupta, Q, Raja Sen, Rajshree Ojha, Sandeep Mohan, Sudhish Kamath and Suparn Verma. It will feature actor-director Rajat Kapoor and Anshuman Jha in the lead. X is not an anthology, but one whole story with the eleven directors making sections of it.', 'tt5207116', '/8L4urL1dImTMR3PtVSk3fZTA84U.jpg');
