insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (3566, 'Batman Begins', False, False, False, True, 2005, 8.2, 'Driven by tragedy, billionaire Bruce Wayne dedicates his life to uncovering and defeating the corruption that plagues his home, Gotham City.  Unable to work within the system, he instead creates a new identity, a symbol of fear for the criminal underworld - The Batman.', 'tt0372784', '/1P3ZyEq02wcTMd3iE4ebtLvncvH.jpg');
