insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (3568, 'Kill Bill: Vol. 1', False, False, False, True, 2003, 8.1, 'An assassin is shot at the altar by her ruthless employer, Bill and other members of their assassination circle – but ''The Bride'' lives to plot her vengeance. Setting out for some payback, she makes a death list and hunts down those who wronged her, saving Bill for last.', 'tt0266697', '/v7TaX8kXMXs5yFFGR41guUDNcnB.jpg');
