insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (3802, 'Walking Tall', False, False, False, True, 1973, 7, 'Buford Pusser''s a wrestler, whose wife wants him to settle down, so they go to his home town in Tennessee, where he plans to get into business with his father. But he is shocked to discover all sorts of graft and corruption going on. And when he is a victim of it and decides to strike back by running against the corrupt sheriff. And he wins and wages his own little war against them', 'tt0070895', '/fL0fuJ5qdm6JVwtIyw6F7e8PZMe.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (3804, 'Pet Sematary', False, True, False, True, 2019, 5.7, 'Dr. Louis Creed''s family moves into the country house of their dreams and discover a pet cemetery at the back of their property. The cursed burial ground deep in the woods brings the dead back to life -- with "minor" problems. At first, only the family''s cat makes the return trip, but an accident forces a heartbroken father to contemplate the unthinkable.', 'tt0098084', '/8ZtH1V4rhrtP53ezlEWrCaMALXF.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (3805, 'The Overnight', False, True, False, True, 2015, 6.1, 'Alex, Emily, and their son, RJ, are new to Los Angeles. A chance meeting at the park introduces them to the mysterious Kurt, Charlotte, and Max. A family “playdate” becomes increasingly interesting as the night goes on.', 'tt3844362', '/fqwFjHmVV7hBtH0YOnPM5WJNmKS.jpg');
