insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4094, 'Once Upon a Time in Venice', False, False, False, True, 2017, 5.3, 'Steve Ford is a private detective in Venice Beach, Calif., who''s good with the ladies, bad with the punches and wild about his dog Buddy. When local thugs steal Buddy, Ford turns to Spyder, their devious leader, and forges an unlikely alliance. With help from his best friend, Steve pulls out the big guns to retrieve Spyder''s stolen cash and cocaine and save Buddy.', 'tt4694544', '/5rr7tQfzS0ARqEea3W9SNSFGRzb.jpg');
