insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4138, 'Barbie in A Mermaid Tale 2', False, False, False, True, 2012, 6, 'Surf''s up for Barbie as she returns as Merliah, the fun and fashionable surfing champion who''s also a magical mermaid princess! In this exciting sea-quel, Merliah makes a splash when she heads to Australia for the ultimate surfing competition. When the evil mermaid Eris escapes from her whirlpool with plans to take over the throne of Oceana, Merliah and her sea friends dive in to stop her. It''s a fresh new adventure where Merliah learns that anything is possible and she really can have the best of both worlds!', 'tt2197823', '/iVCfTtVDAECpbMSokSNilIR6wPr.jpg');
