insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4204, 'White Night Wedding', False, False, False, True, 2008, 6.7, 'Jon, a middle-aged professor is going to get married tomorrow, for the second time, to one of his ex-students half his age. But it''s not all roses. First, there''s his cranky mother-in-law-to-be who violently opposes the marriage and who demands repayment of Jon''s loan before the wedding night. Second, his plans to build a golf course on the little island of Flatey where they live aren''t going at all to plan. Third, his extremely drunk best man is on the loose without any shoes and lastly, the continual presence of his emotional first wife is haunting his every move. When the guests start flocking to the island, Jon starts getting cold feet. After a very long night of drinking and thinking, will Jon be able to make it to the church on time?', 'tt1114712', '/dq8oZHlhR45o06zlf40b41LSouC.jpg');
