insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4515, 'The Last Waltz', False, True, False, False, 1978, 8.2, 'Martin Scorsese''s rockumentary intertwines footage from "The Band''s" incredible farewell tour with probing backstage interviews and featured performances by Eric Clapton, Bob Dylan, Joni Mitchell, Ringo Starr and other rock legends.', 'tt0077838', '/5EBPjgYBjnYNz8twqi8CLZ9QiFT.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4516, 'Lars and the Real Girl', False, True, False, False, 2007, 7.3, 'Sometimes you find love where you''d least expect it. Just ask Lars, a sweet but quirky guy who thinks he''s found the girl of his dreams in a life-sized doll named Bianca. Lars is completely content with his artificial girlfriend, but when he develops feelings for Margo, an attractive co-worker, Lars finds himself lost in a unique love triangle, hoping to somehow discover the real meaning of true love.', 'tt0805564', '/nkAt4a7KIPc7Fi1BhxNHhYYbe2b.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4517, 'The Look of Silence', False, True, False, False, 2014, 8.3, 'A family that survives the genocide in Indonesia confronts the men who killed one of their brothers.', 'tt3521134', '/AnqvpqBEp1Aze64tnMbHpvHV7E9.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4518, 'Cold War', False, True, False, False, 2018, 7.6, 'They say Hong Kong is the safest city in Asia. But tonight, a police van has been hijacked along with the arms, equipment and the five officers aboard, one of the hostages being the only son of police deputy commissioner M.B. Lee! With the chief commissioner away on a conference overseas, the fiery Lee immediately takes command of the rescue operation – codenamed "Cold War" – but the mysterious kidnappers clearly know the police procedures and are therefore in an advantageous position. Objecting to Lee''s aggressive but futile methods, the other deputy commissioner Sean Lau  steps in at this time of crisis to lead the operation. But even Lau''s careful plan to negotiate with the kidnappers ends up a failure, losing the $50 million cash ransom and the life of his lieutenant in the process. Meanwhile, the ICAC (Independent Commission Against Corruption) begins to investigate Lau and Lee on suspicion of having hidden ties to the kidnappers...', 'tt2186715', '/szD25OCr28SkV4ifGMTvm3kUvu.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4519, 'Bone Tomahawk', False, True, False, False, 2015, 7.1, 'During a shootout in a saloon, Sheriff Hunt injures a suspicious stranger. One of the villagers takes care of him in prison. One day they both disappear – only the spear of a cannibal tribe is found. Hunt and a few of his men go in search of the prisoner and his nurse.', 'tt2494362', '/4MmTHpn2Y8emqvBgvOjufImUmKZ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4521, 'Veer-Zaara', False, True, False, False, 2004, 7.8, 'The story of the love between Veer Pratap Singh, an Indian, and Zaara Hayaat Khan, a Pakistani...a love so great it knows no boundaries...', 'tt0420332', '/j267YqLvKAHeyykTflxzpgVgviQ.jpg');
