insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4523, 'Paterson', False, True, False, False, 2016, 7.4, 'Set in the present in Paterson, New Jersey, this is a tale about a bus driver and poet.', 'tt5247022', '/AuJ1ZlfqwuAr9H5Qr1U9KILylse.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4524, 'Fiddler on the Roof', False, True, False, False, 1971, 8, 'This lavishly produced and critically acclaimed screen adaptation of the international stage sensation tells the life-affirming story of Tevye (Topol), a poor milkman whose love, pride and faith help him face the oppression of turn-of-the-century Czarist Russia. Nominated for eight Academy Awards.', 'tt0067093', '/v65PHx7Q6Jx0anyNeUOX07SJic9.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4525, 'The Return of the Living Dead', False, True, False, False, 1985, 7.3, 'When a bumbling pair of employees at a medical supply warehouse accidentally release a deadly gas into the air, the vapors cause the dead to re-animate as they go on a rampage seeking their favorite food: brains!', 'tt0089907', '/qVTFBabgnWz4jZ8wOQRYZI5EITF.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4526, 'The Long Goodbye', False, True, False, False, 1973, 7.6, 'Detective Philip Marlowe tries to help a friend who is accused of murdering his wife.', 'tt0070334', '/oBhUK54yBJ0aH6u9zCzSV5iV7OP.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4527, 'Black Book', False, True, False, False, 2006, 7.7, 'In the Nazi-occupied Netherlands during World War II, a Jewish singer infiltrates the regional Gestapo headquarters for the Dutch resistance.', 'tt0389557', '/fKBeM6vjMD79G3wAXSpw3pBHsit.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4528, 'Legends of the Fall', False, True, False, False, 1994, 7.6, 'An epic tale of three brothers and their father living in the remote wilderness of 1900s USA and how their lives are affected by nature, history, war, and love.', 'tt0110322', '/t1KPGlW0UGd0m515LPQmk2F4nu1.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4529, 'Clue', False, True, False, False, 1985, 7.3, 'Clue finds six colorful dinner guests gathered at the mansion of their host, Mr. Boddy -- who turns up dead after his secret is exposed: He was blackmailing all of them. With the killer among them, the guests and Boddy''s chatty butler must suss out the culprit before the body count rises.', 'tt0088930', '/aRxbYOYHS8T73nzR8hsLousoplR.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4531, 'My Name Is Nobody', False, True, False, False, 1973, 7.5, 'Jack Beauregard, one of the greatest gunman of the Old West, only wants to retire in peace and move to Europe. But a young gunfighter, known as "Nobody", who idolizes Beauregard, wants him to go out in glory. So he arranges for Jack to face the 150-man gang known as The Wild Bunch and earn his place in history.', 'tt0070215', '/rCoO8HqyJiFMhsAOUjjnw8gMgj9.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4533, 'Highlander', False, True, False, False, 1986, 7.1, 'He fought his first battle on the Scottish Highlands in 1536. He will fight his greatest battle on the streets of New York City in 1986. His name is Connor MacLeod. He is immortal.', 'tt0091203', '/8Z8dptJEypuLoOQro1WugD855YE.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4534, 'The Italian Job', False, True, False, False, 1969, 7.3, 'Charlie''s got a ''job'' to do. Having just left prison he finds one his of friends has attempted a high risk job in Torino, Italy, right under the nose of the mafia. Charlie''s friend doesn''t get very far, so Charlie takes over the ''job''. Using three Mini Coopers, a couple of Jaguars and a bus, he hopes to bring Torino to a standstill, steal a fortune in gold and escape in the chaos.', 'tt0064505', '/8NOw3HPHkcBndJW2K6ofbcSb7WD.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4535, 'I Am Not Your Negro', False, True, False, False, 2017, 7.8, 'Working from the text of James Baldwin’s unfinished final novel, director Raoul Peck creates a meditation on what it means to be Black in the United States.', 'tt5804038', '/zwd0Zti7BvY1mO0mTPzM0fRrtc6.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4536, 'The Ghost Writer', False, True, False, False, 2010, 7.2, 'A writer stumbles upon a long-hidden secret when he agrees to help former British Prime Minister Adam Lang complete his memoirs on a remote island after the politician''s assistant drowns in a mysterious accident. In director Roman Polanski''s tense drama, the author realizes that his discovery threatens some very powerful people who will do anything to ensure that certain episodes from Lang''s past remain buried.', 'tt1139328', '/jloADdyw2jhlM9MupO3BhFUUzgD.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4537, 'The Birdcage', False, True, False, False, 1996, 7.1, 'A gay cabaret owner and his drag queen companion agree to put up a false straight front so that their son can introduce them to his fiancé''s right-wing moralistic parents.', 'tt0115685', '/bEyK1K1EgWapeKgkcOLC1ejl9nj.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4538, 'Three Days of the Condor', False, True, False, False, 1975, 7.4, 'A bookish CIA researcher finds all his co-workers dead, and must outwit those responsible until he figures out who he can really trust.', 'tt0073802', '/zinwtZqdb7gnc4zMu8dfkK1fMN3.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4539, 'My Man Godfrey', False, True, False, False, 1936, 8, 'The eccentric Bullock household again need a new butler. Daughter Irene encounters bedraggled Godfrey Godfrey at the docks and, fancying him and noticing his obviously good manners, gets him the job. He proves a great success, but keeps his past to himself. When an old flame turns up Irene''s sister Cordelia starts making waves.', 'tt0050738', '/5D4Ia5G6CS8FAok4WoeToF6ayBk.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4540, 'Murder on the Orient Express', False, True, False, False, 1974, 7.3, 'Agatha Christie''s classic whodunit speeds into the twenty-first century. World-famous sleuth Hercule Poirot has just finished a case in Istanbul and is returning home to London onboard the luxurious Orient Express. But, the train comes to a sudden halt when a rock slide blocks the tracks ahead. And all the thrills of riding the famous train come to a halt when a man discovered dead in his compartment, stabbed nine times. The train is stranded. No one has gotten on or gotten off. That can only mean one thing: the killer is onboard, and it is up to Hercule Poirot to find him. [from imdb.com]', 'tt0279250', '/8ChjqcnOxo30p8o6ehANvKKn8Fr.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4541, 'Indie Game: The Movie', False, True, False, False, 2012, 7.6, 'Indie Game: The Movie is a feature documentary about video games, their creators and the craft. The film follows the dramatic journeys of video game developers as they create and release their games to the world. The film tells the emotional story of friends Edmund McMillen &amp; Tommy Refenes, as they craft their first Xbox game: "Super Meat Boy". It follows Phil Fish, the creator of the highly-anticipated game: "FEZ". After 4 years of working in near solitude, Phil reveals his opus to the public for the first time. And, the film tells the surprising story of one of the highest-rated video games of all time:"Braid". The film is about making video games, but at its core, it''s about the creative process, and exposing yourself through your work. In short: Making fun and games is anything but fun and games.', 'tt1942884', '/tlMe35ZqZ0SFGNzb4O3CNH8pVgv.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4542, 'The Boondock Saints', False, True, False, False, 1999, 7.8, 'With a God-inspired moral obligation to act against evil, twin brothers Conner and Murphy set out to rid Boston of criminals. However, rather than working within the system, these Irish Americans decide to take swift retribution into their own hands.', 'tt0144117', '/xj3jhyq3ZsfdVn79kXC1XKFVQlv.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4543, 'Miss Sloane', False, True, False, False, 2016, 7.5, 'An ambitious lobbyist faces off against the powerful gun lobby in an attempt to pass gun control legislation.', 'tt4540710', '/ptfvQlqe2xJiMSewSj52qAVq5z0.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4544, 'Ernest & Celestine', False, True, False, False, 2012, 7.9, 'Celestine is a little mouse trying to avoid a dental career; Ernest is a big bear craving an artistic outlet. When Celestine meets Ernest, they overcome their natural enmity by forging a life of crime together.', 'tt1816518', '/uSvv6OiqGAT7MBFsZLxmZv6NYRZ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4546, 'Moonstruck', False, True, False, False, 1987, 7.1, 'Cher is devastatingly funny, sinuous and beautiful as Loretta, an unlucky-in-love Italian widow who finds romance through the intervention of the Manhattan moon. With her wedding to a close friend just weeks away, she meets - and falls hopelessly in love with - his younger brother (Cage)!', 'tt0093565', '/hH5mm3A5r1Bw8uBAJXbAtJxBpPB.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4547, 'An Honest Liar', False, True, False, False, 2014, 7.5, 'An Honest Liar tells the incredible story of the world-famous magician, escape artist, and world-renowned enemy of deception, James ''The Amazing'' Randi. The film brings to life Randi''s intricate investigations that publicly exposed psychics, faith healers, and con-artists with quasi-religious fervor. A master deceiver who came out of the closet at the age of 81, Randi created fictional characters, fake psychics, and even turned his partner of 25 years, Jose Alvarez, into a sham guru names Carlos.', 'tt2246565', '/tn8viCBxP2xdQ7SS2fww7zeoPwe.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4548, 'Sholay', False, True, False, False, 1975, 8.2, 'A Police Officer, whose family was killed by a bandit named Gabbar Singh, decides to fight fire with fire and recruits two convicts, Jai & Veeru, to bring Gabbar Singh to jail alive, but will they succeed in their mission and get their reward? Come and watch the action-packed super-hit Sholay to find out!!', 'tt0073707', '/AfXw3LGbywC4jxpHZ46sMnkh7nl.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4549, 'The Proposition', False, True, False, False, 2005, 7.4, 'Set in the Australian outback in the 1880s, the movie follows the series of events following the horrific rape and murder of the Hopkins family, allegedly committed by the infamous Burns brothers gang. Captain Morris Stanley captures Charlie Burns and gives him 9 days to kill his older dangerous psychopathic brother, or else they''ll hang his younger mentally slow brother on Christmas Day.', 'tt0421238', '/e4j7s9SzaJTfjkorMX1iY38IzZi.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4550, 'Fist of Fury', False, True, False, False, 1972, 7.4, 'Chen Zhen returns to the international compound of China only to learn of his beloved teacher''s death. This is compounded by the continual racist harassment by the Japanese population in the area. Unlike his friends, he confronts it head on with his mastery of martial arts while investigating his teacher''s murder.', 'tt0068767', '/dlcipCOa9hlfBBz7kCAyjsf3q0E.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4551, 'Fitzcarraldo', False, True, False, False, 1982, 8.1, 'The story of Brian Sweeney Fitzgerald, an extremely determined man who intends to build an opera house in the middle of a jungle.', 'tt0083946', '/oBCnYEKcg1rMhr5JjDnrRpilvDd.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4552, 'The Big Country', False, True, False, False, 1958, 7.9, 'Retired, wealthy sea Captain Jame McKay arrives in the vast expanse of the West to marry fiancée Pat Terrill. McKay is a man whose values and approach to life are a mystery to the ranchers and ranch foreman Steve Leech takes an immediate dislike to him. Pat is spoiled, selfish and controlled by her wealthy father, Major Henry Terrill. The Major is involved in a ruthless civil war, over watering rights for cattle, with a rough hewn clan led by Rufus Hannassey. The land in question is owned by Julie Maragon and both Terrill and Hannassey want it.', 'tt0051411', '/q3LPSPn6d43xuaVMob4yMyAl7tt.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4553, 'You Can Count on Me', False, True, False, False, 2000, 7.5, 'A single mother''s life is thrown into turmoil after her struggling, rarely-seen younger brother returns to town.', 'tt0203230', '/7mGFmij1FZD5eUnNwvUjmpqrDl6.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4556, 'Paradise Lost 3: Purgatory', False, True, False, False, 2011, 8.1, 'A further investigation into the arrest of three teenagers convicted of killing three young boys in Arkansas who spent nearly 20 years in prison before being released after new DNA evidence indicated they may be innocent.', 'tt2028530', '/QNpVc4FEFcgQs3cwe7XRsxt8gS.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4557, 'Midnight Special', False, True, False, False, 2016, 6.6, 'A father and son go on the run after the dad learns his child possesses special powers.', 'tt2649554', '/hgDRq1l4ATxwufWjILKsYtglbI6.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4558, 'King of Devil''s Island', False, True, False, False, 2010, 7.5, 'Based on a true story: Norwegian winter, 1915. On the island Bastøy, outside Oslo, a group of young boys aged 11 to 18, are held in an institution for delinquent youth, notorious for its sadistic regime.  One day a new boy, Erling, arrives, determined to escape from the island. After a tragic incident, he ends up leading the boys in a violent uprising. When the boys manage to take over the island, 150 soldiers are sent in to restore order.', 'tt1332134', '/qsNOb119zUplzxWYZdxN1x8GxdI.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4559, 'Scarlet Street', False, True, False, False, 1945, 7.8, 'A man in mid-life crisis befriends a young woman after rescuing her from an attack. She believes he is rich and her boyfriend persuades her to con him out of his fortune.', 'tt0038057', '/pioF72zZq3Qu3p5DkkAq59ZEb6B.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4561, 'Meru', False, True, False, False, 2015, 7.7, 'Meru is the electrifying story of three elite American climbers—Conrad Anker, Jimmy Chin, and Renan Ozturk—bent on achieving the impossible.', 'tt2545428', '/y7PvS0dIBqk1X69Ka6toio0xnaz.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4562, 'The Report', False, True, False, False, 2019, 7.2, 'The movie revolves around the life of a tax collector who is accused of taking bribes, and also has to deal with problems at home, including the suicide attempt of his wife.', 'tt0076098', '/uRnFFsDmE3qpxTlp8wqoAnsaTMO.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4563, 'The Sweet Hereafter', False, True, False, False, 1997, 7.5, 'A small mountain community in Canada is devastated when a school bus accident leaves more than a dozen of its children dead. A big-city lawyer arrives to help the survivors'' and victims'' families prepare a class-action suit, but his efforts only seem to push the townspeople further apart. At the same time, one teenage survivor of the accident has to reckon with the loss of innocence brought about by a different kind of damage.', 'tt0120255', '/gJy7LMq4b5qO5fsSUSoBgm24BHf.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4564, 'Dead Ringers', False, True, False, False, 1988, 7.3, 'Elliot, a successful gynecologist, works at the same practice as his identical twin, Beverly. Elliot is attracted to many of his patients and has affairs with them. When he inevitably loses interest, he will give the woman over to Beverly, the meeker of the two, without the woman knowing the difference. Beverly falls hard for one of the patients, Claire, but when she inadvertently deceives him, he slips into a state of madness.', 'tt0094964', '/74GupdZY2hzpiawRJxBChVhnZLw.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4565, 'Triangle', False, True, False, False, 2009, 6.9, 'Small-timer Fei (Louis Koo), his harried buddy Sam (Simon Yam), and antique store owner Mok (Sun Hong Lei) are all in desperate need of money. Fei wants his friend to drive a robbery getaway car but Sam backs out, throwing Fei in trouble with the triads. As the three are arguing, a mysterious man leaves them a map, leading them to an unlikely treasure under the Legislative Council building. All their financial woes seem to be solved after a late-night heist, but they are being tracked by shady cop Wen (Lam Ka-Tung), who is carrying on an affair with Sam''s emotionally unstable wife, Ling (Kelly Lin), and has connections with Fei. When the twisted relationship tangles come to light, the brotherhood dangerously breaks down and the treasure ends up in the wrong hands.', 'tt0846017', '/2BhKMrlbuwTbaQZAvxRLF7FF7kE.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4566, 'Gallipoli', False, True, False, False, 1981, 7.4, 'As World War I rages, brave and youthful Australians Archy and Frank, both agile runners, become friends and enlist in the Australian and New Zealand Army Corps together. They later find themselves part of the Dardanelles Campaign on the Gallipoli peninsula, a brutal eight-month conflict which pit the British and their allies against the Ottoman Empire and left over 500,000 men dead.', 'tt0082432', '/un7VCqTmjrq3u75mQ204a8tofD4.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4567, 'Terms of Endearment', False, True, False, False, 1983, 7.4, 'Aurora and Emma are mother and daughter who march to different drummers. Beginning with Emma''s marriage, Aurora shows how difficult and loving she can be. The movie covers several years of their lives as each finds different reasons to go on living and find joy. Aurora''s interludes with Garrett Breedlove, retired astronaut and next door neighbor are quite striking.', 'tt0086425', '/iZiYzBb6nyv2anbpVTV5ba6GSwq.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4568, 'Steamboat Bill, Jr.', False, True, False, False, 1928, 7.9, 'The just out of college effete son of a no-nonsense steamboat captain comes to visit his father whom he''s not seen since he was little.', 'tt0019421', '/zygJMsmXxeyDc1N67OCZc8xtq4I.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4569, 'Thunder Road', False, True, False, False, 2018, 7.1, 'Jimmy Arnaud eulogizes his mother.', 'tt5116560', '/ycgibsqsst0vnr9Dv0BVg0vEbtt.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4570, 'Pretty in Pink', False, True, False, False, 1986, 6.8, 'Andie is an outcast, hanging out either with her older boss, who owns the record store where she works, or her quirky high school classmate Duckie, who has a crush on her. When one of the rich and popular kids at school, Blane, asks Andie out, it seems too good to be true. As Andie starts falling for Blane, she begins to realize that dating someone from a different social sphere is not easy.', 'tt0091790', '/jeFJyzJBufJexpOuywuPp8q2tCJ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4571, 'The Kings of Summer', False, True, False, False, 2013, 7.1, 'Joe Toy, on the verge of adolescence, finds himself increasingly frustrated by his single father, Frank''s attempts to manage his life. Declaring his freedom once and for all, he escapes to a clearing in the woods with his best friend, Patrick, and a strange kid named Biaggio. He announces that they are going to build a house there, free from responsibility and parents. Once their makeshift abode is finished, the three young men find themselves masters of their own destiny, alone in the woods.', 'tt2179116', '/6dcIbwJuxlEnzLe0EaGFvsh4ZdS.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4572, 'You Don''t Know Jack', False, True, False, False, 2010, 7.6, 'Angel of mercy… or murderous “Doctor Death”? Jack Kervorkian is one of the most polarizing figures in modern American history, a man whose passionate belief that people have the right to die has brought him both praise and vilification. Oscar®- and Emmy®-winning actor Al Pacino brings “Dr. Death” to life in an all-new HBO Films presentation: You Don’t Know Jack, directed by Oscar®-winner Barry Levinson.', 'tt1132623', '/xl4x8aILt9v91TUOKXHIVDZwkXy.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4574, 'Beautiful Boy', False, True, False, False, 2018, 7.3, 'A married couple on the verge of separation are leveled by the news their 18-year-old son committed a mass shooting at his college, then took his own life.', 'tt1533013', '/7puPuSeje8315Pbdxzo8bxHD6EB.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4575, 'Hard Eight', False, True, False, False, 1996, 7.2, 'A stranger mentors a young Reno gambler who weds a hooker and befriends a vulgar casino regular.', 'tt0119256', '/1l5UaoP25Ak8PWCKIULQz70yF03.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4576, 'Ginger Snaps', False, True, False, False, 2000, 6.8, 'The story of two outcast sisters, Ginger (Katharine Isabelle) and Brigitte (Emily Perkins), in the mindless suburban town of Bailey Downs. On the night of Ginger''s first period, she is savagely attacked by a wild creature. Ginger''s wounds miraculously heal but something is not quite right. Now Brigitte must save her sister and save herself.', 'tt0210070', '/6Ub96RLPuHn95PWiY8F2PjTg9al.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4577, 'Robot & Frank', False, True, False, False, 2012, 7.1, 'A delightful dramatic comedy, a buddy picture, and, for good measure, a heist film. Curmudgeonly old Frank lives by himself. His routine involves daily visits to his local library, where he has a twinkle in his eye for the librarian. His grown children are concerned about their father’s well-being and buy him a caretaker robot. Initially resistant to the idea, Frank soon appreciates the benefits of robotic support – like nutritious meals and a clean house – and eventually begins to treat his robot like a true companion. With his robot’s assistance, Frank’s passion for his old, unlawful profession is reignited, for better or worse.', 'tt1990314', '/602vUwe1GPmaRvcrT3OQWqH1Nm1.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4578, 'Particle Fever', False, True, False, False, 2013, 7.4, 'Physicists are on the cusp of the greatest scientific discovery of all time -- or perhaps their greatest failure.', 'tt1385956', '/3MHs9BrHt6ZjlPvaL4CP1ULYV2Z.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4581, 'Adam''s Apples', False, True, False, False, 2005, 7.8, 'A neo-nazi sentenced to community service at a church clashes with the blindly devotional priest.', 'tt0418455', '/v5KtTI8uFWqOed7dL3nzAvOTCn2.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4582, 'Gia', False, True, False, False, 1998, 7, 'Fact-based story of top fashion model Gia Marie Carangi follows her life from a rebel working in her father''s diner at age 17 to her death in 1986 at age 26 from AIDS, one of the first women in America whose death was attributed to the disease. In between, she followed a downward spiral of drug abuse and failed relationships.', 'tt0123865', '/oFi5OIvBf59W1KFaN0cuiMEEbs1.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4583, 'And the Band Played On', False, True, False, False, 1993, 7.8, 'The story of the discovery of the AIDS epidemic and the political infighting of the scientific community hampering the early fight with it.', 'tt0106273', '/woHcFTdGGzeZdRlEPKMekdw0wll.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4584, 'Sophie Scholl: The Final Days', False, True, False, False, 2005, 7.6, 'True story of Germany''s most famous anti-Nazi heroine brought to thrilling, dramatic life. Sophie Scholl stars Julia Jentsch in a luminous performance as the fearless activist of the underground student resistance group, The White Rose. Armed with long-buried historical records of her incarceration, director Marc Rothemund expertly re-creates the last six days of Sophie Scholl''s life.', 'tt0426578', '/84HWRlRwpOjZ4cYlmFZjDGyvB5q.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4585, 'Hellraiser', False, True, False, False, 1987, 7, 'Larry and his wife, Julia move into an old house and discover a hideous creature - the man''s half-brother, who is also the woman''s former lover - hiding upstairs. Having lost his earthly body to a trio of S&amp;M demons, the Cenobites, Frank, is brought back into existence by a drop of blood on the floor. He soon forces his former mistress to bring him his necessary human sacrifices to complete his body... but the Cenobites won''t be happy about this.', 'tt0093177', '/3Z0oPHyLnk3Vx6ZMC1MiVwIrKhO.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4586, 'The Court Jester', False, True, False, False, 1955, 7.9, 'A 12th century court jester in England becomes involved with a desperate band of outlaws who are attempting to overthrow the king.', 'tt0049096', '/rp15BUWv6hXpjNxGTLfs2EKhFm8.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4587, 'Air Force One', False, True, False, False, 1997, 6.5, 'Russian terrorists conspire to hijack the aircraft with the president and his family on board. The commander in chief finds himself facing an impossible predicament: give in to the terrorists and sacrifice his family, or risk everything to uphold his principles - and the integrity of the nation.', 'tt0118571', '/evO1iENjLpUnbwjnt5XK85jRYob.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4588, 'Get the Gringo', False, True, False, False, 2012, 7, 'A career criminal (Gibson) nabbed by Mexican authorities is placed in a tough prison where he learns to survive with the help of a 9-year-old boy.', 'tt1567609', '/sLzL1xG6JgVEEQ4DqSJpnJw3D2A.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4589, 'Eat Drink Man Woman', False, True, False, False, 1994, 7.8, 'The film tells the story of a retired and widowed Chinese master chef Chu (Si Hung Lung) and his family living in modern day Taipei, Taiwan. At the start of the film, he lives with his three attractive daughters, all of whom are unattached. As the film progresses, each of the daughters encounters new men in their lives. When these new relationships blossom, the stereotypes are broken and the living situation within the family changes.. The film features several scenes displaying the techniques and artistry of gourmet Chinese cooking. Since the family members have difficulty expressing their love for each other, the intricate preparation of banquet quality dishes for their Sunday dinners is the surrogate for their familial feelings.', 'tt0111797', '/ktdCBg2Xq2Ry0fuJYT4izktM2Hg.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4590, 'Big Night', False, True, False, False, 1996, 7.3, 'Primo &amp; Secondo, two immigrant brothers, pin their hopes on a banquet honoring Louis Prima to save their struggling restaurant.', 'tt0115678', '/k8HGfeGfS2z9UDIwBc5xcF4KDWz.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4593, 'Reds', False, True, False, False, 1981, 7.3, 'A radical American journalist becomes involved with the Communist revolution in Russia and hopes to bring its spirit and idealism to the United States.', 'tt0082979', '/3jOs4aWI7XSVIEaF8KMojNre7A7.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4594, 'Bernie', False, True, False, False, 2012, 6.8, 'In this true story in the tiny, rural town of Carthage, TX, assistant funeral director Bernie Tiede was one of the town''s most beloved residents. Everyone loved and appreciated Bernie, and it came as no surprise when he befriended Marjorie Nugent, an affluent widow who was as well known for her sour attitude as her fortune. Until that day news came that Marjorie Nugent had been dead for some time, and Bernie Tiede was being charged with the murder.', 'tt1704573', '/rW5CetooG545jpkPNpD4FjFAXfe.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4595, 'Ink', False, True, False, False, 2009, 6.9, 'Invisible forces exert power over us in our sleep. A mercenary named Ink, on a literal nightmare mission, captures the spirit of 8-year-old Emma in the dream world. To save her, the dream-givers marshal all their resources, focusing on saving the soul of Emma''s tragically broken father.', 'tt1071804', '/9vYyX7c8gZtxuqd6SiRukRb4gRd.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4596, 'Dog Soldiers', False, True, False, False, 2002, 6.8, 'A squad of British soldiers on training in the lonesome Scottish wilderness find a wounded Special Forces captain and the carnaged remains of his team. As they encounter zoologist Megan, it turns out that werewolves are active in the region. They have to prepare for some action as the there will be a full moon tonight...', 'tt0280609', '/cUsSL2x0HYS7QmTwPcEE66b52Wt.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4598, 'The Lost City of Z', False, True, False, False, 2016, 6.6, 'At the dawn of the 20th century, British explorer Percy Fawcett journeys into the Amazon, where he discovers evidence of a previously unknown, advanced civilization that may have once inhabited the region. Despite being ridiculed by the scientific establishment, which views indigenous populations as savages, the determined Fawcett, supported by his devoted wife, son, and aide-de-camp, returns to his beloved jungle in an attempt to prove his case.', 'tt1212428', '/ik3ebv7J18fs6cHkmu91oxz7EGt.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4599, 'Rab Ne Bana Di Jodi', False, True, False, False, 2008, 7.2, 'A lonely 40-ish man, likely to remain a bachelor, has a chance to find the love of his life when he falls for a vivacious young woman.', 'tt1182937', '/m8x6I2qf3R98HtF4DmJXcdxCU64.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4600, 'Soldier''s Girl', False, True, False, False, 2003, 7.9, 'The true story of Soldier’s Girl, which takes place in Fort Campbell, KY, tells the heart-wrenching story of the life and tragic death of soldier Barry Winchell. His love for Calpernia Addams, a beautiful transgendered nightclub performer was misunderstood by fellow soldiers and eventually leads to his brutal death.', 'tt0324013', '/ogxl6v4fPdqsYUwI2HNNNC68BAG.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4601, 'The Endless Summer', False, True, False, False, 1966, 7.7, 'The Endless Summer, by Bruce Brown, is one of the first and most influential surf movies of all times. The film documents American surfers Mike Hynson and Robert August as they travel the world during California’s winter (which back in 1965 was off-season for surfing) in search of the perfect wave and an endless summer.', 'tt0060371', '/5W5uaqQ7NZTwoDMKO4AtdcahHex.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4602, 'Laurence Anyways', False, True, False, False, 2012, 7.7, 'Set in the 1980s and 1990s, a man tries to salvage his relationship with his fiancée after revealing to her his aspirations of becoming a woman.  This is the story of a wild and unusual love.', 'tt1650048', '/d8BpJ9qOQE7VFFqaV1XDdGfMsP7.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4603, 'A Street Cat Named Bob', False, True, False, False, 2016, 7.3, 'Based on the international best selling book. The true feel good story of how James Bowen, a busker and recovering drug addict, had his life transformed when he met a stray ginger cat.', 'tt3606888', '/6ltgCFYvw0wL7460ldFMMZv4txj.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4604, 'Holiday Inn', False, True, False, False, 1942, 7.4, 'Lovely Linda Mason has crooner Jim Hardy head over heels, but suave stepper Ted Hanover wants her for his new dance partner after femme fatale Lila Dixon gives him the brush. Jim''s supper club, Holiday Inn, is the setting for the chase by Hanover and manager Danny Reed. The music''s the thing.', 'tt0034862', '/6HujErFoaOrL2bMWRdHHBevwH9f.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4605, 'The Snowman', False, True, False, False, 1982, 8.2, 'In 1978 Jimmy Graham, a thirty four year old happily married the father of two, scored his dream job with Operation Deepfreeze - training American scientists in survival skills in Antarctica. He left in December of that year. Three months later he arrived back agitated and paranoid. He said that while out on the ice he had stumbled onto a secret American nuclear site and the CIA had given him a chemical lobotomy to keep him quiet. Jimmy rapidly descended into schizophrenia. His behaviour became so frightening that his wife Frances fled to safety, taking their two children Sean and Juliet with her. For thirty years the family lived with this story, but no one ever dug deeper. The man they loved went away sane and came back permanently fractured – his mind was a blizzard. Now Juliet wants to know what exactly did happen on that frozen continent.', 'tt1543053', 'None');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4606, 'A Bridge Too Far', False, True, False, False, 1977, 7.4, 'Tells the story of operation Market Garden. A failed attempt by the allies in the latter stages of WWII to end the war quickly by securing three bridges in Holland allowing access over the Rhine into Germany. A combination of poor allied intelligence and the presence of two crack German panzer divisions meant that the final part of this operation (the bridge in Arnhem over the Rhine) was doomed to failure.', 'tt0075784', '/yi3nRRDlxvfFPfAZ2mSVMok6MnR.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4607, 'Funny Face', False, True, False, False, 1957, 7, 'A shy Greenwich Village book clerk is discovered by a fashion photographer and whisked off to Paris where she becomes a reluctant model.', 'tt0050419', '/1CW8Wn2itBQ7wACBQHpOyIbPpmD.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4608, 'Murderball', False, True, False, False, 2005, 7.7, 'Quadriplegics, who play full-contact rugby in wheelchairs, overcome unimaginable obstacles to compete in the Paralympic Games in Athens, Greece.', 'tt0436613', '/rXqzEpwp0wli8k0N85PP88n3mV3.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4609, 'Wake in Fright', False, True, False, False, 1971, 7.7, 'Wake in Fright is the story of John Grant, a bonded teacher who arrives in the rough outback mining town of Bundanyabba planning to stay overnight before catching the plane to Sydney, but as one night stretches into several he plunges headlong into his own destruction.', 'tt0067541', '/y1mWPdAOQC3ZezWHNscqrUTMl8G.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4610, 'A Most Wanted Man', False, True, False, False, 2014, 6.8, 'When a half-Chechen, half-Russian, tortured half-to-death immigrant turns up in Hamburg''s Islamic community, laying claim to his father''s ill gotten fortune, both German and US security agencies take a close interest: as the clock ticks down and the stakes rise, the race is on to establish this most wanted man''s true identity - oppressed victim or destruction-bent extremist?', 'tt1972571', '/6B76Z5Ct758RfKFoFg37skVRiMp.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4611, 'The Girl Who Played with Fire', False, True, False, False, 2009, 7.2, 'Mikael Blomkvist, publisher of Millennium magazine, has made his living exposing the crooked and corrupt practices of establishment Swedish figures. So when a young journalist approaches him with a meticulously researched thesis about sex trafficking in Sweden and those in high office who abuse underage girls, Blomkvist immediately throws himself into the investigation.', 'tt1216487', '/3v9LgxjEPyJfDDOtkWUJNYnYcfu.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4612, 'Frantz', False, True, False, False, 2016, 7.5, 'In the aftermath of WWI, a young German who grieves the death of her fiancé in France meets a mysterious French man who visits the fiance’s grave to lay flowers.', 'tt5029608', '/tBCL7di8Y1BA9RAzQSoKOjtdPqj.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4614, 'Runaway Train', False, True, False, False, 1985, 7.2, 'A hardened convict and a younger prisoner escape from a brutal prison in the middle of winter only to find themselves on an out-of-control train with a female railway worker while being pursued by the vengeful head of security.', 'tt0089941', '/zAJEDjkghNKgm8rG2W7iy2Oy3OQ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4616, 'House of Sand and Fog', False, True, False, False, 2003, 7.5, 'Behrani, an Iranian immigrant buys a California bungalow, thinking he can fix it up, sell it again, and make enough money to send his son to college. However, the house is the legal property of former drug addict Kathy. After losing the house in an unfair legal dispute with the county, she is left with nowhere to go. Wanting her house back, she hires a lawyer and befriends a police officer. Neither Kathy nor Behrani have broken the law, so they find themselves involved in a difficult moral dilemma.', 'tt0315983', '/oPDcGmhuc44PnPdoJhXRssM10Kj.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4617, 'Behind the Candelabra', False, True, False, False, 2013, 7, 'Based on the autobiographical novel, the tempestuous 6-year relationship between Liberace and his (much younger) lover, Scott Thorson, is recounted.', 'tt1291580', '/iodt6hrewVXxR415PIjBeuZg3Qs.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4618, 'Angst', False, True, False, False, 1983, 7.3, 'A killer is released from prison and breaks into a remote home to kill a woman, her handicapped son and her pretty daughter.', 'tt0165623', '/68pr5cnPYJ3TeHVFzgcWTN30R1m.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4619, 'Fanaa', False, True, False, False, 2006, 7.2, 'Zooni Ali Beg (Kajol) is a blind Kashmiri girl who travels without her parents for the first time with a dance troupe to Delhi to perform in a ceremony for independence day. On her journey, she meets Rehan Khan (Aamir Khan), a casanova and tour guide who flirts with her. Although her friends warn Zooni about him, she cannot resist falling in love with him and he takes her on a private tour of New Delhi. But there is more to Rehan than meets the eye and Zooni will have to make a heartbreaking decision.', 'tt0439662', '/8YCF3drTYLc7eoLFBbbexsT3iQj.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4620, 'Intolerance: Love''s Struggle Throughout the Ages', False, True, False, False, 1916, 7.8, 'The story of a poor young woman, separated by prejudice from her husband and baby, is interwoven with tales of intolerance from throughout history.', 'tt0006864', '/mATFEpn2n7uIYCiWm2DGTn2AixS.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4622, 'Bill Cunningham New York', False, True, False, False, 2011, 7.9, 'Chronicles a man who is obsessively interested in only one thing, the pictures he takes that document the way people dress. The 80-year-old New York Times photographer has two columns in the paper''s Style section, yet nobody knows who he is.', 'tt1621444', '/ex1m4lIAcdxJLBojN90fD3HoxlU.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4623, 'What Maisie Knew', False, True, False, False, 2013, 7.4, 'Based on the Henry James novella, the story frames on 7-year-old Maisie, caught in a custody battle between her mother – a rock and roll icon – and her father. What Maisie Knew is an evocative portrayal of the chaos of adult life seen entirely from a child’s point of view.', 'tt1932767', '/1unkWKdT68ba6NdiSsPqzJeylxc.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4624, 'Citizen X', False, True, False, False, 1995, 7.5, 'Based on the true story of a Russian serial killer who, over many years, claimed victim to over 50 people. His victims were mostly under the age of 17. In what was then a communists state, the police investigations were hampered by bureaucracy, incompetence and those in power. The story is told from the viewpoint of the detective in charge of the case.', 'tt0112681', '/ednsGW73lQHhU9QOrfEAXBgVMvW.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4625, 'Kirikou and the Sorceress', False, True, False, False, 1998, 7.5, 'Drawn from elements of West African folk tales, it depicts how a newborn boy, Kirikou, saves his village from the evil witch Karaba.', 'tt0181627', '/9RNrozaagFmpm6m0CP7q4XDcIXj.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4626, 'Some Kind of Wonderful', False, True, False, False, 1987, 7.1, 'A young tomboy, Watts, finds her feelings for her best friend, Keith, run deeper than just friendship when he gets a date with the most popular girl in school.', 'tt0094006', '/lc6KUGAetADDQNxbz3u5dJ27OSb.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4627, 'The Phantom of the Opera', False, True, False, False, 1925, 7.6, 'Count de Chagnie has discovered Christine''s singing talent on a market place and sent her to his friend Carriere, the director of the Parisian opera. However just when she arrives Carriere''s dismissed. His arrogant successor refuses to let a woman of low birth sing in his opera, but graciously employs Christine as gadrobiere for his wife Charlotta, who''s installed as first singer. He also fights the phantom, an unknown guy who lives since many years in the catacombs below the opera and was granted privileges by Carriere. However the phantom knows how to defend himself and at the same time helps Christine to her career.', 'tt0100361', '/ole8e9YQwvHoKVpWO0lgfLFFemc.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4628, 'Masquerade', False, True, False, False, 2012, 7.8, 'During the confusing and conspiratorial Joseon Dynasty King Gwang-hae orders his councilor, HEO Kyun, to find him a double in order to avoid the constant threat of assassination. HEO Kyun finds Ha-sun, a jester who looks remarkably like the king, and just as feared, Gwang-hae is poisoned. HEO Kyun proposes Ha-sun fill the role as the king until Gwang-hae recovers fully and grooms Ha-sun to look and act every bit the king. While assuming the role of the king at his first official appearance, Ha-sun begins to ponder the intricacies of the problems debated in his court. Being fundamentally more humanitarian than Gwang-hae, Ha-sun’s affection and appreciation of even the most minor servants slowly changes morale in the castle for the better. However, his chief opposition, PARK, notices the sudden shift in the king’s behavior and starts to ask questions.', 'tt2290840', '/htOuhpB44segRvgwkQWyPYvMp7b.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4629, 'Sultan', False, True, False, False, 2016, 7, '', 'tt0264051', '/cwiktG551xKWpPOg1o3TH0WLXpb.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4630, 'Heidi', False, True, False, False, 2015, 7.4, 'After discovering a mysterious doll in an attic, two high school friends are increasingly plagued by a series of disturbing and unexplained events.', 'tt3581478', '/oyMwDaE94RcspWeQStuhPlQW0gH.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4632, 'The Letter', False, True, False, False, 1940, 7.6, 'A playwright who begins to mentally unravel before premiere night. She is plagued by dreams and visions of being watched, but cannot decide if she is at the center of a manipulative plot or simply losing her grip on reality.', 'tt1899324', '/pGDnNEAhttpXLO9ihFmQ44XQhO4.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4633, 'What If', False, True, False, False, 2013, 6.8, 'Wallace, a medical school dropout, has been repeatedly burned by bad relationships. So while everyone around him, including his roommate Allan, seems to be finding the perfect partner, Wallace decides to put his love life on hold.  It is then that he meets Chantry, an animator who lives with her long term boyfriend Ben.  Wallace and Chantry form an instant connection, striking up a close friendship. But there is no denying the chemistry between them, leading the pair to wonder:  what if the love of your life is actually your best friend?', 'tt1486834', '/rK5kb8T4DwQoZkLrQKxvKFDD65q.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4634, 'Truman', False, True, False, False, 2015, 7.3, 'When Julián receives an unexpected visit in Madrid from his lifelong friend Tomás, who now lives in Canada, the encounter is bittersweet. This reunion, their first meeting in many years, may also be their last. Diagnosed with terminal cancer, Julián is focused on putting his affairs in order, while Tomás is still grasping at hope. For four intense days, the two men, accompanied by Julián''s faithful dog, Truman, tour the city sharing emotional, hilarious and surprising moments. Full of laughter and sadness, and led by brilliant performances by Ricardo Darin and Javier Cámara, Truman is a wonderfully touching film on how we say goodbye.', 'tt3754940', '/nH7L2zNPGUGlrSUl7KHI9Xxup4s.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4637, 'Flickering Lights', False, True, False, False, 2000, 7.7, 'Four small gangsters from Copenhagen trick a gangster boss: they take over 4,000,000 kroner which they were supposed to bring him. Trying to escape to Barcelona they are forced to stop in the countryside, in an old, wrecked house, hiding there for several weeks. Slowly, one after another, they realize, that they would like to stay there, start a new life.', 'tt0236027', '/dGVHnZw0T7B5idyPk3LwurPSLbQ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4639, 'The Band''s Visit', False, True, False, False, 2007, 7.5, 'Once-not long ago-a small Egyptian police band arrived in Israel. Not many remember this... It wasn''t that important. A band comprised of members of the Egyptian police force head to Israel to play at the inaugural ceremony of an Arab arts center, only to find themselves lost in the wrong town.', 'tt1032856', '/gZPUNtSWnn23Zd88wxL5xzqYYqy.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4640, 'One Day in September', False, True, False, False, 1999, 7.8, 'The full story of the 1972 Munich Olympics Massacre and the Israeli revenge operation ''Wrath of God.'' The 1972 Munich Olympics were interrupted by Palestinian terrorists taking Israeli athletes hostage. Besides footage taken at the time, we see interviews with the surviving terrorist, Jamal Al Gashey, and various officials detailing exactly how the police, lacking an anti-terrorist squad and turning down help from the Israelis, botched the operation', 'tt0230591', '/vsxtkcidi7flgxWERBSnwtyH1JJ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4641, 'The Doors', False, True, False, False, 1991, 7.2, 'The story of the famous and influential 1960''s rock band and its lead singer and composer, Jim Morrison.', 'tt0101761', '/14Jw0oYnpRoJ2BPoJwje3wrNOwp.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4642, 'Turbo Kid', False, True, False, False, 2015, 6.7, 'In a post-apocalyptic wasteland, an orphaned teen must battle a ruthless warlord to save the girl of his dreams.', 'tt3672742', '/wE06nw6PyF48Nb1hA9mPoZTpB5u.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4644, '4 Moons', False, True, False, False, 2014, 7.5, 'Four stories about love and self-acceptance: An eleven year-old boy struggles to keep secret the attraction he feels towards his male cousin. Two former childhood friends reunite and start a relationship that gets complicated due to one of them’s fear of getting caught. A gay long lasting relationship is in jeopardy when a third man comes along. An old family man is obsessed with a young male prostitute and tries to raise the money to afford the experience.', 'tt2260850', '/j1zyHN8FggqDlwMjVbBwbVJZ7pe.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4646, 'The Pink Panther Strikes Again', False, True, False, False, 1976, 7.2, 'Now seriously mentally ill after working with Clouseau for such a long time, Inspector Dreyfus escapes from the mental asylum he was being held in and vows to destroy Clouseau forever. He kidnaps an eminent scientist and forces him to build a machine capable of destroying the world, with the intention of doing so unless Clouseau is delivered to him.', 'tt0075066', '/4W2r5CFXjD05AgYX8QxZQ18jdSk.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4648, 'Transsiberian', False, True, False, False, 2008, 6.7, 'A Trans-Siberian train journey from China to Moscow becomes a thrilling chase of deception and murder when an American couple encounters a mysterious pair of fellow travelers.', 'tt0800241', '/dhTOda5CLp9tEdDzQZiqBTdv5lR.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4650, 'The Spy Who Came in from the Cold', False, True, False, False, 1965, 7.6, 'British agent Alec Leamas refuses to come in from the cold war during the 1960s, choosing to face another mission, which may prove to be his final one.', 'tt0059749', '/gq4rjEdNO68kQh98yEihWAnJBO8.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4651, 'House of Games', False, True, False, False, 1987, 7.3, 'A psychiatrist comes to the aid of a compulsive gambler and is led by a smooth-talking grifter into the shadowy but compelling world of stings, scams, and con men.', 'tt0093223', '/pIWXSYrXlMcULRwmSHyO36RTHrq.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4652, 'Gleason', False, True, False, False, 2016, 8.4, 'The story begins at the height of Gleason''s career. He has it all: women, wealth, and extraordinary power. But he is haunted by memories of his childhood. Gleason spends his formative years entering amateur contests, performing in sleazy night spots. Along the way, he steals gags from the best comics in town and finds love with Genevieve, a dancer whom he marries. But Gleason isn''t the ideal husband or even a responsible father as he abandons his family to answer the call of Hollywood. Brash, arrogant, and egotistical, he alienates his directors and the man who discovers him. When he ends up back in New York, Gleason gets one of those rare second chances in the new medium of television, creating some of its most unforgettable characters. But even as Gleason becomes the talk of the tube, his life - ruled by demons of rage, booze, and insecurity - unravels.', 'tt0322422', '/t36PIi1eaXWA0wROxrGaXAznsJn.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4653, 'The Central Park Five', False, True, False, False, 2012, 7.7, 'In 1989, five black and Latino teenagers from Harlem were arrested and later convicted of raping a white woman in New York City''s Central Park. They spent between 6 and 13 years in prison before a serial rapist confessed that he alone had committed the crime, leading to their convictions being overturned. Set against a backdrop of a decaying city beset by violence and racial tension, this is the story of that horrific crime, the rush to judgment by the police, a media clamoring for sensational stories and an outraged public, and the five lives upended by this miscarriage of justice.', 'tt2380247', '/aKlQ8Sjr07OkCLuYkRYtpVSMm3e.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4654, 'The Most Dangerous Game', False, True, False, False, 1932, 7.1, 'When legendary hunter Bob Rainsford is shipwrecked on the perilous reefs surrounding a mysterious island, he finds himself the guest of the reclusive and eccentric Count Zaroff. While he is very gracious at first, Zaroff eventually forces Rainsford and two other shipwreck survivors, brother and sister Eve and Martin Towbridge, to participate in a sadistic game of cat and mouse in which they are the prey and he is the hunter.', 'tt0023238', '/3aNlOIFlGOnIzxqvAWpgm5Sly1H.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4655, 'Priceless', False, True, False, False, 2006, 7, 'James, down on his luck and desperate for some quick cash, agrees to drive a small truck across country. He soon realizes that he''s made a huge mistake and has inadvertently become involved in a dangerous human trafficking ring. The unlikely hero risks it all to shut down the trafficking ring and save the woman he is falling in love with.', 'tt4978274', '/byI0wd12in6Wjo8YoSzlNIJdqyJ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4656, 'House on Haunted Hill', False, True, False, False, 1959, 6.9, 'A remake of the 1959 film of the same name. A millionaire offers a group of diverse people $1,000,000 to spend the night in a haunted house with a horrifying past.', 'tt0185371', '/weiGTsZqBglKxkIn5xj1nEjxn81.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4657, 'The Crazies', False, True, False, False, 2010, 6.5, 'Four friends find themselves trapped in their small hometown after they discover their friends and neighbors going quickly and horrifically insane.', 'tt0455407', '/fiJFGiqXUelHJ9Ms8mtFOCsFf4B.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4659, 'The Treatment', False, True, False, False, 2014, 7.2, 'Inspector Nick Cafmeyer seems to have it all - looks, brains and a successful career.  But a dark cloud hangs over his life: since the age of nine, he has been haunted by the unsolved disappearance of his younger brother, Bjorn. Plettinckx, a known sex offender, was questioned but quickly released. Plettinckx lives close by and takes fiendish pleasure in harassing Nick. Then, when a disturbing case comes to light involving a missing nine-year-old, Nick heads a massive search which turns into a relentless manhunt.', 'tt3089778', '/nzVhjAjEaugzs0CSmEO50W5mTcc.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4661, 'The Decline of Western Civilization', False, True, False, False, 1981, 7.6, 'The Los Angeles punk music scene circa 1980 is the focus of this film. With Alice Bag Band, Black Flag, Catholic Discipline, Circle Jerks, Fear, Germs, and X.', 'tt0082252', '/kfPIZYynaPCuDqbqIs4EKbsAfPE.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4662, 'Atlantic City', False, True, False, False, 1980, 7.3, 'Lou (Burt Lancaster), a small time gangster who thinks he used to be something big, meets and falls for a sexy casino employee named Sally (Susan Sarandon). She needs his help realizing her dream of going to Monte Carlo, a symbol of the glamorous life that she has been looking for so desperately.', 'tt0080388', '/emZLwACcXh4FHAWWkpYb9lsC0Q2.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4663, 'Tom Petty and the Heartbreakers: Runnin'' Down a Dream', False, True, False, False, 2007, 8.6, 'Directed by Peter Bogdanovich and packed with rare concert footage and home movies, this documentary explores the history of Tom Petty and the Heartbreakers, including Petty''s famous collaborations and notorious clashes with the record industry. Interviews with musical luminaries including Jackson Browne, George Harrison, Eddie Vedder, Roger McGuinn, Jeff Lynne, Dave Stewart and Petty himself shed some revelatory vision.', 'tt0965382', '/aT4ePhptnlxfOLt3mB8dlbchYMk.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4665, 'Suspiria', False, True, False, False, 2018, 6.8, 'From the moment she arrives in Freiberg, Germany, to attend the prestigious Tans Academy, American ballet-dancer Suzy Bannion senses that something horribly evil lurks within the walls of the age-old institution.', 'tt0076786', '/5ya8jTbNZTrCFUx9OwpNBjCivXY.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4667, 'King of New York', False, True, False, False, 1990, 7, 'A former drug lord returns from prison determined to wipe out all his competition and distribute the profits of his operations to New York''s poor and lower classes in this stylish and ultra violent modern twist on Robin Hood.', 'tt0099939', '/p6fVO9NTXrye1yogN0hMSJwpKrV.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4668, 'Three… Extremes', False, True, False, False, 2004, 7, '“Three” is an anthology of three horror shorts from three different Asian countries.In "Dumplings" an aging actress wishing to reclaim her youth goes to a woman who makes dumplings that supposedly have regenerative properties; however, they contain a gruesome secret ingredient. In "Box" a soft spoken young woman has a bizarre recurring nightmare about being buried in a box in the snow. Searching for her long lost sister, she realizes her dreams and reality may possibly be connected. In "The Cut" a successful film director and his wife are kidnapped by an extra, who forces the director to play his sadistic games. If he fails, his wife’s fingers will be chopped off one by one every five minutes', 'tt0420251', '/et67QHZlzgbPwU4zC7o2iwRIrcz.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4669, 'The Invisible War', False, True, False, False, 2012, 7.6, 'An investigative and powerfully emotional documentary about the epidemic of rape of soldiers within the US military, the institutions that perpetuate and cover up its existence, and its profound personal and social consequences.', 'tt2120152', '/ph9Lo3pMNmTXFQa9wZVdCKL2tRe.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4670, 'We Were Here', False, True, False, False, 2011, 7.9, '''We Were Here'' is the first film to take a deep and reflective look back at the arrival and impact of AIDS in San Francisco, and how the City''s inhabitants dealt with that unprecedented calamity. It explores what was not so easy to discern in the midst of it all - the parallel histories of suffering and loss, and of community coalescence and empowerment.', 'tt1787837', '/ltm5hF4APoD4CLod7YZor4nYofs.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4671, 'Carnival of Souls', False, True, False, False, 1962, 7.1, 'Alex Grant is a troubled young woman, 20 years ago, she witnessed her mother get murdered by a carnival clown. She has nightmares of the clown - Louis Seagram. First a kind man then turned bad and also tried to rape Alex''s mother. Alex fears for her life, worrying that Louis might come after her and her younger sister. One day Louis returns after being released from prison for murdering Alex''s mother and tries to kill Alex. Alex dries the car that she and Louis are in into the river, then she begins to have hallucinations, she begins to lose her mind', 'tt0120627', '/k5gPDGcSOQh0W5W2Gfum3D9msCV.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4672, 'The Whistleblower', False, True, False, False, 2010, 7.1, 'Lee Jang-hwan receives widespread acclaim and media attention after successfully cloning human embryo stem cells. A TV news program PD, Yoon Min-cheol, receives a phone call from an anonymous source who says he has worked with Dr. Lee on the stem cell project. The source blows the whistle on Lee''s work, revealing how Lee fabricated research results and engaged in unethical practices.', 'tt4034414', '/fizySa53ba70jdoEbSeONQlu5nF.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4673, 'Accattone', False, True, False, False, 1961, 7.8, 'A pimp with no other means to provide for himself finds his life spiralling out of control when his prostitute is sent to prison.', 'tt0054599', '/vYFrdrYHwUFT1ibBFmSJfB6lPQE.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4674, 'Run Silent, Run Deep', False, True, False, False, 1958, 7.3, 'The captain of a submarine sunk by the Japanese during WWII is finally given a chance to skipper another sub after a year of working a desk job. His singleminded determination for revenge against the destroyer that sunk his previous vessel puts his new crew in unneccessary danger.', 'tt0052151', '/jVlkFYqhrgl6QpWbo6MlnRD5z6N.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4675, 'Shoeshine', False, True, False, False, 1946, 8, 'At a track near Rome, shoeshine boys are watching horses run. Two of the boys Pasquale, an orphan, and Giuseppe, his younger friend are riding. The pair have been saving to buy a horse of their own to ride...', 'tt0038913', '/lsXCNNbI15hZ3ATiWMx3o4hCK76.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4676, 'Opera', False, True, False, False, 1987, 7, 'A young opperata is stalked by a deranged fan bent on killing the people associated with her to claim her for himself.', 'tt0093677', '/8GwQYftf0yonDL3JTuJsyqABmQE.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4677, 'The Tillman Story', False, True, False, False, 2010, 7.7, 'Pat Tillman never thought of himself as a hero. His choice to leave a multimillion-dollar football contract and join the military wasn''t done for any reason other than he felt it was the right thing to do. The fact that the military manipulated his tragic death in the line of duty into a propaganda tool is unfathomable and thoroughly explored in Amir Bar-Lev''s riveting and enraging documentary.', 'tt1568334', '/8858FsyVcEuuVQSI9KB3KFQpycF.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4678, 'Dhoom 2', False, True, False, False, 2006, 6.5, 'In the second installment of the Dhoom series, Jai and Ali are back as the buddy cops on their toughest job so far. "Mr.A" is an international thief who has planned to steal a priceless artifact in Mumbai and the police have got to nab him. But Mr.A manages to steal the artifact and elude the police. He finds his match in Sunehri and they form a partnership. They move on to Rio for their next job, with Jai and Ali hot on their trail. Mr.A whose actual name is Aryan, and Sunehri are drawn towards each other but little does Aryan know of Sunehri''s little secret.', 'tt0441048', '/4gCOksuevXLmAca4ecJIlucCmHi.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4679, 'White Light/Black Rain: The Destruction of Hiroshima and Nagasaki', False, True, False, False, 2007, 8.3, 'Steven Okazaki presents a deeply moving look at the painful legacy of the first -- and hopefully last -- uses of thermonuclear weapons in war. Featuring interviews with fourteen atomic bomb survivors - many who have never spoken publicly before - and four Americans intimately involved in the bombings, White Light/Black Rain provides a detailed exploration of the bombings and their aftermath.', 'tt0911010', '/43YQd9JMZvgjEtIBuC27EsAYyKP.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4680, 'The Man from Earth', False, True, False, False, 2007, 7.9, 'An impromptu goodbye party for Professor John Oldman becomes a mysterious interrogation after the retiring scholar reveals to his colleagues he never ages and has walked the earth for 14,000 years.', 'tt0756683', '/V086R82gNgWrotaXZFO4JhdgB1.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4682, 'Harry Brown', False, True, False, False, 2009, 7.2, 'An elderly ex-serviceman and widower looks to avenge his best friend''s murder by doling out his own form of justice.', 'tt1289406', '/zthprdjuK7vN4ABBGQXo8RF7qYB.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4683, 'The Girl Who Kicked the Hornet''s Nest', False, True, False, False, 2009, 7.3, 'After taking a bullet to the head, Salander is under close supervision in a hospital and is set to face trial for attempted murder on her eventual release. With the help of journalist Mikael Blomkvist and his researchers at Millennium magazine, Salander must prove her innocence. In doing this she plays against powerful enemies and her own past.', 'tt1343097', '/92byRQBuuiLcfKnBi5NOuipSgV8.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4684, 'Ghost Town', False, True, False, False, 2008, 6.7, 'Dentist, Bertram Pincus is a man whose people skills leave much to be desired. When Pincus dies unexpectedly, but is miraculously revived after seven minutes, he wakes up to discover that he now has the annoying ability to see ghosts.', 'tt0995039', '/hRYAPIVT1b33ldEyOYJUQUtqbyf.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4686, 'Friday the 13th', False, True, False, False, 1980, 6.5, 'Ignoring the warnings of the locals, a group of teenage camp counselors takes on the job of reopening Camp Crystal Lake — on Friday the 13th no less, and raise the ire of Jason Voorhees, a masked, homicidal maniac.', 'tt0758746', '/fLTQgFpOJA4iyuazewxPof13xSY.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4687, 'Sabotage', False, True, False, False, 1937, 7.1, 'In "Sabotage", Arnold Schwarzenegger leads an elite DEA task force that takes on the world''s deadliest drug cartels. When the team successfully executes a high-stakes raid on a cartel safe house, they think their work is done - until, one-by-one, the team members mysteriously start to be eliminated. As the body count rises, everyone is a suspect.', 'tt1742334', '/f5L0WlOAzFiKTOGNZTcKkEJ4iOT.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4688, 'Pyaasa', False, True, False, False, 1957, 8.5, 'Vijay, a destitute poet, searches for true love while attempting to get his work published. With the assistance of two women in his life, his dream comes true - but at a cost that causes Vijay to rethink his worldview.', 'tt0050870', '/hS3NbehcPfJ5XElqjZXVD9wsDk1.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4690, 'Birdy', False, True, False, False, 1984, 7.3, 'Two young men are seriously affected by the Vietnam war. One of them has always been obsessed with birds - but now believes he really is a bird, and has been sent to a mental hospital. Can his friend help him pull through?', 'tt0086969', '/29pXKXcPANhOTxyFqTGEaw7nIws.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4691, 'The Secret of Roan Inish', False, True, False, False, 1995, 7.5, '10-year-old Fiona is sent to live with her grandparents in a small fishing village in Donegal, Ireland. She soon learns the local legend that an ancestor of hers married a Selkie - a seal who can turn into a human. Years earlier, her baby brother was washed out to sea and never seen again, so when Fiona spies a naked little boy on the abandoned Isle of Roan Inish, she is compelled to investigate..', 'tt0111112', '/knrhzhfMKgTqrDrCy3nBWL5sypa.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4692, 'Meet John Doe', False, True, False, False, 1941, 7.7, 'As a parting shot, fired reporter Ann Mitchell prints a fake letter from unemployed "John Doe," who threatens suicide in protest of social ills. The paper is forced to rehire Ann and hires John Willoughby to impersonate "Doe." Ann and her bosses cynically milk the story for all it''s worth, until the made-up "John Doe" philosophy starts a whole political movement.', 'tt0033891', '/AmbFL6IYyyIMNuwbwCx8qolhAgD.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4693, 'Death of a Salesman', False, True, False, False, 1985, 7.3, 'An aging salesman is fired from his job after a long career in it. Broken, without much to look forward to, he tries reconnecting with his wife and kids who he had always put down as he dedicated himself to work.', 'tt0229328', '/48poz1d9PxCMTh0qEUo1UgRNKId.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4695, 'Kannathil Muthamittal', False, True, False, False, 2002, 8.4, 'A little girl is told by her parents that she is adopted. Determined to find her birth mother, her family eventually agrees to take her to Sri Lanka, where they are abducted by the terrorist group known as the Tamil Tigers.', 'tt0312859', '/idMs0lyC3bGbehUhDxQZ3V0q5Wa.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4696, 'Polytechnique', False, True, False, False, 2009, 7.2, 'A dramatization of the Montreal Massacre of 1989 where several female engineering students were murdered by an unstable misogynist.', 'tt1194238', '/k0xmtct9cSseksuFKMSXxM8hfni.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4697, 'The King''s Choice', False, True, False, False, 2016, 7.1, 'On 9 April 1940, German soldiers arrive in the city of Oslo. The King of Norway faces a choice that will change his country forever.  The King''s Choice is a story about the three most dramatic days in Norway''s history, the royal family''s escape and King Haakon''s difficult choice after Nazi Germany''s invasion of Norway.', 'tt4353996', '/9NNxwavpQoaBTt696o0QHKoUM7g.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4698, 'Who Killed the Electric Car?', False, True, False, False, 2006, 7.6, 'In 1996, electric cars began to appear on roads all over California. They were quiet and fast, produced no exhaust, and ran without gasoline... Ten years later, these cars were destroyed.', 'tt0489037', '/bELDMlz8TTTPvZzZAbaUD83fGC8.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4699, 'Stuck in Love', False, True, False, False, 2012, 7.2, 'An acclaimed writer, his ex-wife, and their teenaged children come to terms with the complexities of love in all its forms over the course of one tumultuous year.', 'tt2205697', '/6vG0eCHT2e4uH035g1s6JTouU6R.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4700, 'A Star Is Born', False, True, False, False, 1937, 7.3, 'A Star Is Born is a 1976 rock music film telling the story of a young woman, played by Barbra Streisand who enters show business, and meets and falls in love with an established male star, played by Kris Kristofferson, only to find her career ascending while his goes into decline.', 'tt0075265', '/oVpUzCimMZ0ecni7lDuYPuRdxoQ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4702, 'Bad Boy Bubby', False, True, False, False, 1993, 7.4, 'Bad Boy Bubby is just that: a bad boy. So bad, in fact, that his mother has kept him locked in their house for his entire thirty years, convincing him that the air outside is poisonous. After a visit from his estranged father, circumstances force Bubby into the waiting world, a place which is just as unusual to him as he is to the world.', 'tt0106341', '/oqgDRgHfMW99nSdVgulpT2PRNmt.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4703, 'Breakdown', False, True, False, False, 1997, 6.9, 'When his SUV breaks down on a remote Southwestern road, Jeff Taylor lets his wife, Amy, hitch a ride with a trucker to get help. When she doesn''t return, Jeff fixes his SUV and tracks down the trucker -- who tells the police he''s never seen Amy. Johnathan Mostow''s tense thriller then follows Jeff''s desperate search for his wife, which eventually uncovers a small town''s murderous secret.', 'tt0118771', '/h6POHvMnoqBVpAFQXCMBoTjru9.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4704, 'The House of the Devil', False, True, False, False, 2009, 6.4, 'In the 1980s, college student Samantha Hughes takes a strange babysitting job that coincides with a full lunar eclipse. She slowly realizes her clients harbor a terrifying secret.', 'tt1172994', '/4abKUbc9IbsndbaY1hacN1iPM0Y.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (4706, 'Waco: The Rules of Engagement', False, True, False, False, 1997, 7.9, 'In one of the most tragic face-offs in the history of law enforcement, the deadly debacle at Waco pitted the Branch Davidian sect against the FBI in an all-out war. This documentary makes the most of footage and recordings to examine how the events that led to the tragedy of April 19, 1993, unfolded, and how the FBI''s unrelenting approach made what was already a bad situation much worse.', 'tt0120472', '/eKHB7yftbvdY3OYd5QvZCHvEJcF.jpg');
