insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5081, '11:55', False, True, False, False, 2017, 4.9, 'Upon his return home, a U.S. Marine must face up to the consequences of a shameful tragedy that has defined his past before he runs out of time.', 'tt4250438', '/cq8YSZwPXK0M4HwofurhNOfUaFt.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5083, 'Cobra Verde', False, True, False, False, 1987, 7, '"Cobra Verde" (Green Snake) is a villainous bandit who is hated and feared in his homeland, Brazil. Alienated, discontent, and with little to lose, he accepts an extremely dangerous mission to reopen a slave trade with the Kingdom of Dahomey in West Africa. T', 'tt0094888', '/7e4mZU0M90j63L6DalUfZHGsmuv.jpg');
