insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5085, 'Sweet Land', False, True, False, False, 2005, 7.1, 'Winner of the Audience Award for Best Narrative Feature at the 2005 Hamptons International Film Festival, Sweet Land is a poignant and lyrical celebration of land, love, and the American immigrant experience. Based on Will Weaver’s short story A Gravestone Made of Wheat and shot on location in Southern Minnesota.', 'tt0428038', '/qYxbJ0pWyuRBqVXrrhaDQnFAsNN.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5086, 'T-Men', False, True, False, False, 1948, 7, 'Two U.S. Treasury ("T-men") agents go undercover in Detroit, and then Los Angeles, in an attempt to break a U.S. currency counterfeiting ring.', 'tt0039881', '/eb2rHqWg6o1Lnqhm72Qt9ZuU22r.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5088, 'Twist of Faith', False, True, False, False, 2005, 7.2, 'Jacob Fisher, an Orthodox Jewish Cantor and amateur songwriter who resides in Brooklyn, New York, witnesses the senseless murder of his wife and three children. Catatonic, Jacob walks out on his Jewish mourning ritual, leaving behind his Jewish garments, wallet and keys. Abandoning his identity, he wanders aimlessly, hoping somehow to understand his tragedy. Fortuitously, he lands in Brent, Alabama (population 2,500), where he is embraced by Nina, a single mother and the lead singer of a small gospel choir, her Uncle Moe and their gospel community.', 'tt2585068', '/3eHtcJaYmEjO2tG5YT8mmUlLrej.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5090, 'Super Inframan', False, True, False, False, 1975, 6.3, 'The surface of the Earth is under attack, thousands of people are killed in this unprovoked attacked. The cause, Princess Dragonmon and her army of monsters have decided to invade. Princess Dragonmon is an alien whose race has been hiding under us for centuries waiting to attack and the time is right. A doctor has been preparing for something like this and turns his assistant Rayma into the cyborg hero known as Inframan. Now only Inframan stands between the Earth and Princess Dragonmon but when a close friend is captured and brainwashed, can she be stopped with this inside man feeding her info?', 'tt0073168', '/zjJdp0MHMguviRY4ZY6kvdImTQp.jpg');
