insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5093, 'Cash Only', False, True, False, False, 2015, 6.1, 'Elvis Martini is in deep shit. His dilapidated Detroit apartment building is about to be foreclosed on by the bank; most of his tenants are behind on rent; he''s in big debt to bookies and his daughter''s school. He finds some ill-gotten cash in an evicted tenant''s apartment and it briefly keeps the wolves at bay, but it brings out a bigger wolf, one that wants his stolen money back. When his daughter is kidnapped by the mysterious menace he''s accidentally messed with, Elvis has 24 hours to come up with $25K - cash only.', 'tt2883448', '/hq2vcgpDUTDXp0jK1SATLfVD4oM.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5096, 'The English Surgeon', False, True, False, False, 2009, 8.1, 'This documentary offers a glimpse into the life of an English neurosurgeon (Henry Marsh) situated in Ukraine as we are exposed to the overwhelming dilemmas he has to face and the burden he has to carry throughout his profession.', 'tt1200060', '/ftgAqnOwzWdoROC1A9sYTPaz3xz.jpg');
